mapper.select:
SELECT A.RecordDeptCode AS "RecordDeptCode" , A.RecordProductName AS "RecordProductName" , A.ProductYear AS "ProductYear" , A.SeriesName AS "SeriesName" , A.SeriesCode AS "SeriesCode" , A.RecordSeq AS "RecordSeq" , A.RecordCount AS "RecordCount" , A.RecordSubject AS "RecordSubject" , A.RecordType AS "RecordType" , A.RecordTypeTxt AS "RecordTypeTxt" , A.EndYear AS "EndYear" , A.KeepPeriod AS "KeepPeriod" , A.KeepPeriodTxt AS "KeepPeriodTxt" , A.KeepMethod AS "KeepMethod" , A.KeepMethodTxt AS "KeepMethodTxt" , A.KeepPlace AS "KeepPlace" , A.KeepPlaceTxt AS "KeepPlaceTxt" , A.RecordClass AS "RecordClass" , A.RecordClassTxt AS "RecordClassTxt" , A.EditCheck AS "EditCheck" , A.EditCheckTxt AS "EditCheckTxt" , A.RecordRegisteredCount AS "RecordRegisteredCount" , A.RecordPageCount AS "RecordPageCount" , A.RecordFileCount AS "RecordFileCount" , A.TakeoverCheck AS "TakeoverCheck" , A.TakeoverCheckTxt AS "TakeoverCheckTxt" , A.RecordClassNum AS "RecordClassNum" , A.SeriesPath AS "SeriesPath" , A.RecordStatus AS "RecordStatus" , A.RecordStatusTxt AS "RecordStatusTxt" , A.ProvideYN AS "ProvideYN" , A.WorkCharger AS "WorkCharger" , A.RegistDate AS "RegistDate" , A.RCnt AS "RCnt" FROM( SELECT GRG.RecordDeptCode , GRG.RecordProductName , GRG.ProductYear , GRG.SeriesName , GRG.SeriesCode , GRG.RecordSeq , GRG.RecordCount , GRG.RecordSubject , GRG.RecordType , CASE GRG.RecordType WHEN '1' THEN '일반문서' WHEN '2' THEN '도면류' WHEN '3' THEN '사진·필름류 시청각기록물 ' WHEN '4' THEN '녹음·동영상류 시청각기록물' WHEN '5' THEN '카드류' ELSE '' END AS RecordTypeTxt , GRG.EndYear , GRG.KeepPeriod , CASE GRG.KeepPeriod WHEN '01' THEN '01년' WHEN '03' THEN '03년' WHEN '05' THEN '05년' WHEN '10' THEN '10년' WHEN '20' THEN '20년' WHEN '25' THEN '25년' WHEN '30' THEN '준영구' WHEN '40' THEN '영구' ELSE '' END AS KeepPeriodTxt , GRG.KeepMethod , CASE GRG.KeepMethod WHEN '1' THEN '원본과 보존매체를 함께 보존하는 방법' WHEN '2' THEN '원본은 폐기하고 보존매체만 보존하는 방법' WHEN '3' THEN '원본을 그대로 보존하는 방법' ELSE '' END AS KeepMethodTxt , GRG.KeepPlace , CASE GRG.KeepPlace WHEN '1' THEN '자료관' WHEN '2' THEN '전문관리기관' ELSE '' END AS KeepPlaceTxt , GRG.RecordClass , CASE GRG.RecordClass WHEN '1' THEN '신기록물' WHEN '2' THEN '구기록물' ELSE '' END AS RecordClassTxt , GRG.EditCheck , CASE GRG.EditCheck WHEN '0' THEN '해당없음' WHEN '1' THEN '수정함' ELSE '' END AS EditCheckTxt , GRG.RecordRegisteredCount , GRG.RecordPageCount , GRG.RecordFileCount , GRG.TakeoverCheck , CASE GRG.TakeoverCheck WHEN '0' THEN '해당없음' WHEN '1' THEN '인수' WHEN '2' THEN '인계' ELSE '' END AS TakeoverCheckTxt , GRG.RecordClassNum , GRG.SeriesPath , GRG.RecordStatus , CASE GRG.RecordStatus WHEN '0' THEN '처리 중' WHEN '1' THEN '편철 중' WHEN '2' THEN '편철' WHEN '3' THEN '현황보고완료' WHEN '4' THEN '이관완료' WHEN '5' THEN '폐지' WHEN '6' THEN '현황보고반려' WHEN '7' THEN '이관반려' WHEN '8' THEN '현황보고오류' WHEN '9' THEN '이관오류' ELSE '' END AS RecordStatusTxt , GRG.ProvideYN , GRG.WorkCharger , GRG.RegistDate , NVL(GRD.RCnt, 0) AS RCnt FROM GOV_RECORD_GFILE GRG LEFT OUTER JOIN ( SELECT COUNT(*) AS RCnt , GRD.RecordClassNum FROM GOV_RECORD_DOC GRD GROUP BY GRD.RecordClassNum ) GRD ON GRD.RecordClassNum = GRG.RecordClassNum ) A
AND A.RecordClassNum = #{recordClassNum}
AND A.RecordDeptCode = #{deptCode}
AND A.SeriesCode = #{seriesCode}
AND A.TakeoverCheck = #{takeOverCheck}
AND A.RecordStatus = #{recordStatus}
AND A.EndYear = #{baseYear}
AND A.RecordStatus IN ('1', '5')
AND A.RecordStatus IN ('0', '2', '3', '4', '6', '7', '8', '9')
AND ( A.RecordSubject LIKE '%'||#{searchWord}||'%' OR A.SeriesName LIKE '%'||#{searchWord}||'%' )
AND A.RecordSubject LIKE '%' || #{searchWord} || '%'
AND A.SeriesName LIKE '%' || #{searchWord} || '%'
, A.RecordSubject , A.RecordStatusTxt , A.RCnt , A.RecordProductName , A.SeriesName , A.EndYear , A.KeepPeriodTxt , A.TakeoverCheckTxt , A.RecordSeq , A.RegistDate
ASC DESC

SELECT COUNT(*) FROM( SELECT RecordDeptCode , RecordProductName , ProductYear , SeriesName , SeriesCode , RecordSeq , RecordCount , RecordSubject , RecordType , CASE RecordType WHEN '1' THEN '일반문서' WHEN '2' THEN '도면류' WHEN '3' THEN '사진·필름류 시청각기록물 ' WHEN '4' THEN '녹음·동영상류 시청각기록물' WHEN '5' THEN '카드류' ELSE '' END AS RecordTypeTxt , EndYear , KeepPeriod , CASE KeepPeriod WHEN '01' THEN '01년' WHEN '03' THEN '03년' WHEN '05' THEN '05년' WHEN '10' THEN '10년' WHEN '20' THEN '20년' WHEN '30' THEN '준영구' WHEN '40' THEN '영구' ELSE '' END AS KeepPeriodTxt , KeepMethod , CASE KeepMethod WHEN '1' THEN '원본과 보존매체를 함께 보존하는 방법' WHEN '2' THEN '원본은 폐기하고 보존매체만 보존하는 방법' WHEN '3' THEN '원본을 그대로 보존하는 방법' ELSE '' END AS KeepMethodTxt , KeepPlace , CASE KeepPlace WHEN '1' THEN '자료관' WHEN '2' THEN '전문관리기관' ELSE '' END AS KeepPlaceTxt , RecordClass , CASE RecordClass WHEN '1' THEN '신기록물' WHEN '2' THEN '구기록물' ELSE '' END AS RecordClassTxt , EditCheck , CASE EditCheck WHEN '0' THEN '해당없음' WHEN '1' THEN '수정함' ELSE '' END AS EditCheckTxt , RecordRegisteredCount , RecordPageCount , RecordFileCount , TakeoverCheck , CASE TakeoverCheck WHEN '0' THEN '해당없음' WHEN '1' THEN '인수' WHEN '2' THEN '인계' ELSE '' END AS TakeoverCheckTxt , RecordClassNum , SeriesPath , RecordStatus , CASE RecordStatus WHEN '1' THEN '편철 중' WHEN '2' THEN '편철' WHEN '3' THEN '현황보고완료' WHEN '4' THEN '이관완료' WHEN '5' THEN '폐지' ELSE '' END AS RecordStatusTxt FROM GOV_RECORD_GFILE GRG ) A
AND A.RecordClassNum = #{recordClassNum}
AND A.RecordDeptCode = #{deptCode}
AND A.SeriesCode = #{seriesCode}
AND A.TakeoverCheck = #{takeOverCheck}
AND A.RecordStatus = #{recordStatus}
AND A.EndYear = #{baseYear}
AND A.RecordStatus IN ('1', '5')
AND A.RecordStatus IN ('0', '2', '3', '4', '6', '7', '8', '9')
AND ( A.RecordSubject LIKE '%'||#{searchWord}||'%' OR A.SeriesName LIKE '%'||#{searchWord}||'%' )
AND A.RecordSubject LIKE '%' || #{searchWord} || '%'
AND A.SeriesName LIKE '%' || #{searchWord} || '%'

mapper.insert:
INSERT INTO GOV_RECORD_GFILE
(
	  RecordSubject
	, RecordDeptCode
	, RecordProductName
	, SeriesCode
	, SeriesName
	, ProductYear
	, RecordSeq
	, RecordCount
	, RecordType
	, EndYear
	, KeepPeriod
	, KeepMethod
	, KeepPlace
	, WorkCharger
	, RecordClass
	, EditCheck
	, RecordRegisteredCount
	, RecordPageCount
	, RecordFileCount
	, TakeoverCheck
	, RecordClassNum
	, SeriesPath
	, RecordStatus
	, RegisterID
	, RegistDate
)
VALUES
(
	  #{RecordSubject}
	, #{RecordDeptCode}
	, #{RecordDeptName}
	, #{SeriesCode}
	, #{SeriesName}
	, TO_CHAR(SYSDATE, 'YYYY')
	, #{RecordSeq}
	, LPAD('1', 3, '0')
	, #{RecordType}
	, #{EndYear}
	, #{KeepPeriod}
	, #{KeepMethod}
	, #{KeepPlace}
	, #{WorkCharger}
	, '1'
	, '0'
	, LPAD('1', 3, '0')
	, LPAD('1', 6, '0')
	, LPAD('1', 6, '0')
	, '0'
	, #{RecordClassNum}
	, #{SeriesPath}
	, '1'
	, #{userCode}
	, SYSDATE
)

mapper.update:
UPDATE GOV_RECORD_GFILE
SET   RecordSubject = #{RecordSubject}
	, RecordDeptCode = #{RecordDeptCode}
	, RecordProductName = #{RecordDeptName}
	, SeriesCode = #{SeriesCode}
	, SeriesName = #{SeriesName}
	, RecordType = #{RecordType}
	, EndYear = #{EndYear}
	, KeepPeriod = #{KeepPeriod}
	, KeepMethod = #{KeepMethod}
	, KeepPlace = #{KeepPlace}
	, WorkCharger = #{WorkCharger}
	, SeriesPath = #{SeriesPath}
	, ModifierID = #{userCode}
	, ModifyDate = SYSDATE
WHERE 1 = 1
AND RecordClassNum = #{RecordClassNum}

mapper.insert:
INSERT INTO GOV_RECORD_GFILE_HISTORY
(
	  RecordClassNum
	, ModifierID
	, ModifyDate
	, ModifyReason

		, BeforeSubject
		, AfterSubject


		, BeforeType
		, AfterType


		, BeforeKeepPeriod
		, AfterKeepPeriod

)
SELECT	#{RecordClassNum}
	  , #{userCode}
	  , SYSDATE
	  , #{ModifyReason}

		 , RecordSubject
	 	 , #{RecordSubject}


		 , RecordType
	  	 , #{RecordType}


		 , KeepPeriod
	  	 , #{KeepPeriod}

FROM GOV_RECORD_GFILE
WHERE RecordClassNum = #{RecordClassNum}

mapper.update:
UPDATE GOV_RECORD_GFILE
SET RecordStatus = #{RecordStatus}
WHERE 1 = 1
AND RecordClassNum IN

	#{item}

mapper.select:
SELECT RecordSeq AS "RecordSeq"
FROM GOV_RECORD_GFILE
WHERE 1=1
AND ProductYear = #{ProductYear}
AND RecordDeptCode = #{RecordDeptCode}
AND SeriesCode = #{SeriesCode}
AND ROWNUM = 1
ORDER BY RecordSeq DESC
