mapper.select:
SELECT CommentID, MemberOf, TargetServiceType, TargetID, `Comment`, Context, LikeCount, ReplyCount,
'Anonymous' AS RegisterCode,
RegisterCode,
RegistDate, DeleteDate, Reserved1, Reserved2, Reserved3, Reserved4, Reserved5, (SELECT COUNT(L.LikeID) FROM covi_smart4j.sys_like AS L WHERE TargetServiceType = 'Comment' AND CommentID = L.TargetID AND L.RegisterCode = #{UserCode}) AS MyLikeCount, (CASE WHEN RegisterCode = #{UserCode} THEN 'Y' ELSE 'N' END) AS AnonymousAuthYn FROM covi_smart4j.sys_comment WHERE ( MemberOf = #{memberOf}
AND TargetServiceType IN ('Board', 'Doc', 'Community')
AND TargetServiceType = #{targetServiceType}
AND #{lastCommentID} > CommentID
AND TargetID = #{targetID} AND DeleteDate IS NULL )
OR (TargetServiceType = #{targetServiceType}
AND #{lastCommentID} > CommentID
AND TargetID = #{targetID} AND DeleteDate IS NOT NULL AND ReplyCount > 0)
ORDER BY CommentID DESC LIMIT #{rowCount}

mapper.update:


UPDATE covi_smart4j.sys_comment
SET Context=#{context}
WHERE CommentID = #{commentID}



UPDATE covi_smart4j.sys_comment
SET ReplyCount = (
	SELECT count(*) FROM covi_smart4j.sys_comment
	WHERE memberOf = #{commentID}
	AND DeleteDate IS NULL
)
WHERE CommentID = #{commentID}


UPDATE covi_smart4j.sys_comment
SET LikeCount=#{likeCount}
WHERE CommentID = #{commentID}


        UPDATE covi_smart4j.sys_comment
		SET DeleteDate=now(3)
		WHERE CommentID = #{commentID}

UPDATE  covi_smart4j.sys_comment 
SET `Comment` = #{comment}
	, Context = #{context}
WHERE CommentID = #{commentID}

mapper.select:
SELECT RegisterCode
FROM covi_smart4j.sys_comment
WHERE CommentID = #{memberOf}
