mapper.select:
SELECT 'group' AS itemType ,gr.CompanyCode ,gr.GroupCode ,gr.GroupType ,gr.MultiDisplayName AS GroupName ,cn.MultiDisplayName AS CompanyName ,gr.PrimaryMail ,gr.MemberOf ,gr.GroupCode AS AN ,gr.MultiDisplayName AS DN ,gr.PrimaryMail AS EM ,cn.CompanyCode AS ETID ,cn.MultiDisplayName AS ETNM ,gr.MemberOf AS SGAN ,gr.MemberOf AS SGDN ,gr.Receivable AS RCV ,gr.SortPath ,gr.GroupPath ,gr.GroupID ,(SELECT COUNT(0) FROM covi_smart4j.sys_object_group AS self WHERE self.MemberOf =gr.GroupCode AND self.IsUse = 'Y') AS hasChild , REPLACE(covi_smart4j.sys_getFullPath(gr.GroupCode),'@','>') AS GroupFullPath FROM covi_smart4j.sys_object_group AS gr LEFT JOIN covi_smart4j.sys_object_group AS cn ON gr.CompanyCode = cn.GroupCode AND cn.GroupType = 'Company'
AND gr.IsUse = 'Y' AND gr.IsDisplay = 'Y' AND (gr.MemberOf != 'NOUSE' OR gr.MemberOf is null) #향후 그룹의 IsUse나 IsDisplay이를 변경한 경우 해당 그룹에 속한 다른 그룹들도 상태 변경하도록 한다. (운영용) AND (gr.MemberOf != 'NOUNIT' OR gr.MemberOf is null) AND gr.GroupType IN ('Dept','Company')
AND gr.CompanyCode =#{companyCode}
AND gr.MemberOf = #{searchText}
AND gr.DisplayName LIKE CONCAT('%',#{searchText} ,'%')
SortPath asc

SELECT 'group' AS itemType ,gr.CompanyCode ,gr.GroupCode ,gr.GroupType ,gr.MultiDisplayName AS GroupName ,cn.MultiDisplayName AS CompanyName ,gr.PrimaryMail ,gr.MemberOf ,gr.GroupCode AS AN ,gr.MultiDisplayName AS DN ,gr.PrimaryMail AS EM ,cn.CompanyCode AS ETID ,cn.MultiDisplayName AS ETNM ,gr.MemberOf AS SGAN ,gr.MemberOf AS SGDN ,gr.Receivable AS RCV ,gr.SortPath ,gr.GroupPath ,gr.GroupID ,(SELECT COUNT(0) FROM covi_smart4j.sys_object_group AS self WHERE self.MemberOf =gr.GroupCode AND self.IsUse = 'Y') AS hasChild , REPLACE(covi_smart4j.sys_getFullPath(gr.GroupCode),'@','>') AS GroupFullPath FROM covi_smart4j.sys_object_group AS gr LEFT JOIN covi_smart4j.sys_object_group AS cn ON gr.CompanyCode = cn.GroupCode AND cn.GroupType = 'Company'
AND gr.IsUse = 'Y' AND gr.IsDisplay = 'Y' AND (gr.MemberOf != 'NOUSE' OR gr.MemberOf is null) #향후 그룹의 IsUse나 IsDisplay이를 변경한 경우 해당 그룹에 속한 다른 그룹들도 상태 변경하도록 한다. (운영용) AND (gr.MemberOf != 'NOUNIT' OR gr.MemberOf is null) AND gr.GroupType != 'Dept'
AND gr.CompanyCode =#{companyCode}
AND gr.DisplayName LIKE CONCAT('%',#{searchText} ,'%')
AND (gr.GroupPath REGEXP(SELECT GROUP_CONCAT(concat(';', GroupCode) SEPARATOR ';|') FROM sys_object_group WHERE Reserved5 IN
#{item}
) OR gr.GroupType = 'Company')
AND ((gr.GroupPath REGEXP(CONCAT('_Community', ';')) AND gr.GroupCode = (SELECT CU_Code FROM COMMUNITY WHERE CU_ID = #{communityId}) OR gr.Reserved5 = 'Community') OR (gr.GroupPath NOT REGEXP(CONCAT('_Community', ';'))) )
SortPath asc

SELECT GroupID, GroupCode, MultiDisplayName FROM covi_smart4j.sys_object_group WHERE IsUse = 'Y' AND IsDisplay = 'Y' AND GroupType = 'Company'
AND GroupCode IN
#{item}
AND GroupCode = #{companyCode}
ORDER BY SortKey

SELECT 'group' AS itemType ,gr.CompanyCode ,gr.GroupCode ,gr.GroupType ,gr.MultiDisplayName AS GroupName ,cn.MultiDisplayName AS CompanyName ,gr.PrimaryMail ,gr.MemberOf ,gr.GroupCode AS AN ,gr.MultiDisplayName AS DN ,gr.PrimaryMail AS EM ,cn.CompanyCode AS ETID ,cn.MultiDisplayName AS ETNM ,gr.MemberOf AS SGAN ,gr.MemberOf AS SGDN ,gr.Receivable AS RCV ,gr.SortPath ,gr.GroupPath ,gr.GroupID ,(SELECT COUNT(0) FROM covi_smart4j.sys_object_group AS self WHERE self.MemberOf =gr.GroupCode AND self.IsUse = 'Y' AND self.IsDisplay = 'Y') AS hasChild , REPLACE(covi_smart4j.sys_getFullPathLang(gr.GroupCode,#{lang}),'@','>') AS GroupFullPath , gr.Approvable AS Approvable , gr.Receivable AS Receivable , gr.ISMAIL AS "IsMail" FROM covi_smart4j.sys_object_group AS gr LEFT JOIN covi_smart4j.sys_object_group AS cn ON gr.CompanyCode = cn.GroupCode AND cn.GroupType = 'Company'
AND gr.IsUse = 'Y' AND gr.IsDisplay = 'Y' AND (gr.MemberOf != 'NOUSE' OR gr.MemberOf is null) #향후 그룹의 IsUse나 IsDisplay이를 변경한 경우 해당 그룹에 속한 다른 그룹들도 상태 변경하도록 한다. (운영용) AND (gr.MemberOf != 'NOUNIT' OR gr.MemberOf is null)
AND gr.GroupType IN ('Dept','Company')
AND gr.GroupType != 'Dept'
AND (gr.GroupPath REGEXP(SELECT GROUP_CONCAT(concat(';', GroupCode) SEPARATOR ';|') FROM sys_object_group WHERE Reserved5 IN
#{item}
) OR gr.GroupType = 'Company')
and gr.GroupType NOT IN ('Authority','Community') AND SUBSTRING_INDEX(gr.GroupCode, '_', -1) NOT IN ('Authority','Community')
AND gr.GroupPath like concat(#{myGroupPath},'%')
AND (gr.MemberOf = #{companyCode} OR gr.GroupCode = #{companyCode}
OR #{defaultValueGroupPath} LIKE CONCAT('%;',gr.MemberOf,';%')
OR #{userGroupPath} LIKE CONCAT('%;',gr.MemberOf,';%')
)
AND gr.CompanyCode =#{companyCode}
AND gr.DisplayName LIKE CONCAT('%',#{searchText} ,'%')
SortPath asc

SELECT CompanyCode AS "CompanyCode", GroupCode AS "GroupCode", GroupPath AS "GroupPath", MultiDisplayName AS "MultiDisplayName" FROM sys_object_group WHERE GroupCode IN
#{item}
ORDER BY SortPath ASC
