mapper.select:
SELECT IPAddress , OS , Browser , DATE_FORMAT(LogonDate, '%Y-%m-%d %H:%i:%s') AS LogonDate , DATE_FORMAT(LogoutDate, '%Y-%m-%d %H:%i:%s') AS LogoutDate FROM covi_smart4j.log_connect WHERE LogonID = #{userCode}
AND LogonDate >= #{startDate}
AND LogonDate <= #{endDate}
IPAddress OS Browser LogoutDate LogonDate
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}


   		SELECT COUNT(*)
		FROM covi_smart4j.log_connect
		WHERE LogonID = #{userCode}


		AND LogonDate >= #{startDate}  	   


		AND LogonDate <= #{endDate}

mapper.insert:
