	, #{isOpt}
	, #{portalID}
	, #{description}
	, #{userCode}
	, NOW(3)
)

	SELECT LAST_INSERT_ID();

mapper.update:
UPDATE covi_smart4j.sys_license
SET	  LicName = #{licName}
	, LicModule= #{licModule}
	, IsOpt = #{isOpt}


	, InitPortal = #{portalID}


	, InitPortal = null


	, IsMbPortal = #{isMbPortal}
