mapper.insert:
	    INSERT INTO covi_smart4j.sys_access_url (URL, IsUse, Description, RegisterCode, RegistDate)
	    VALUES(#{url}, #{isUse}, #{description}, #{registerCode}, now(3))


            SELECT LAST_INSERT_ID();

mapper.update:
UPDATE covi_smart4j.sys_access_url
SET URL = #{url}
  , IsUse = #{isUse}
  , Description = #{description}
  , ModifierCode = #{modifierCode}
  , ModifyDate = now(3)
WHERE AccessUrlID = #{accessURLID}

UPDATE covi_smart4j.sys_access_url
SET  IsUse = #{isUse}
  , ModifierCode = #{modifierCode}
  , ModifyDate = now(3)
WHERE AccessUrlID = #{accessURLID}

mapper.delete:
	    DELETE FROM covi_smart4j.sys_access_url
	    WHERE AccessUrlID IN 

          #{accessURLID}
