mapper.select:
SELECT A.CalendarID , A.SolarDate , A.DN_ID , A.LunarDate , A.AnniversaryType , A.Anniversary , B.MultiDisplayName FROM covi_smart4j.sys_calendar AS A INNER JOIN covi_smart4j.sys_object_domain AS B ON A.DN_ID = B.DomainID WHERE A.Anniversary IS NOT NULL
AND A.DN_ID = #{domainID}
AND A.DN_ID IN
#{domainID}
AND A.AnniversaryType = #{anniversaryType}
AND YEAR(A.SolarDate) >= #{startYear}
A.SolarDate ASC
B.MultiDisplayName A.Anniversary A.SolarDate A.LunarDate A.AnniversaryType A.CalendarID
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT COUNT(*)
FROM covi_smart4j.sys_calendar AS A 
INNER JOIN covi_smart4j.sys_object_domain AS B ON A.DN_ID = B.DomainID
WHERE A.Anniversary IS NOT NULL

	AND A.DN_ID = #{domainID}


 	AND A.DN_ID IN 

	    #{domainID}



	AND A.AnniversaryType = #{anniversaryType}


	 AND YEAR(A.SolarDate) >= #{startYear}

mapper.update:
    	UPDATE covi_smart4j.sys_calendar
		SET 	Anniversary = #{hidAnniversaryNameDic}
				, AnniversaryType = #{anniversaryType}
		WHERE CalendarID = #{calendarID}

