mapper.select:
SELECT LogSeq, TargetSeq, Level , REPLACE(SUBSTR(Message, 1, 200), '\r\n', '') AS Message , LoggingTime FROM covi_smart4j.sys_dbsync_log a WHERE EXISTS ( SELECT 'X' FROM covi_smart4j.sys_dbsync_target WHERE TargetSeq = a.TargetSeq AND DomainID = #{DomainID} )
LoggingTime BETWEEN #{startDate} AND #{endDate}
Level LoggingTime LogSeq
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}


	SELECT COUNT(*)
	FROM covi_smart4j.sys_dbsync_log a
	WHERE EXISTS (
		SELECT 'X' FROM covi_smart4j.sys_dbsync_target
		WHERE TargetSeq = a.TargetSeq
		AND DomainID = #{DomainID}
	)

    	LoggingTime BETWEEN #{startDate} AND #{endDate}


SELECT Message
FROM covi_smart4j.sys_dbsync_log a
WHERE LogSeq = #{LogSeq}

mapper.insert:

CREATE TABLE IF NOT EXISTS ${TargetDatabase}.${TargetTableNm}_Sync
AS SELECT * FROM ${TargetDatabase}.${TargetTableNm}
WHERE 1 != 1

mapper.select:
SELECT a.TargetSeq , a.TargetName , a.SrcTableNm , a.SrcDatabase , a.LastSyncTime , a.LastStatus , a.UseYn , a.DomainID , a.TargetDatabase , a.TargetTableNm , a.SortSeq , a.ConditionSQL , a.Description , a.DatasourceSeq , a.ModifyDate
, b.ConnectionPoolName , c.DomainCode , covi_smart4j.Fn_BaseGetDictionary_S(#{lang}, c.MultiDisplayName) AS DomainName FROM covi_smart4j.sys_dbsync_target a JOIN covi_smart4j.sys_datasource b ON a.datasourceseq = b.datasourceseq JOIN covi_smart4j.sys_object_domain c ON c.domainid = a.domainid
a.DomainID = #{DomainID}
DomainName TargetName ConnectionPoolName LastStatus LastSyncTime UseYn TargetSeq ModifyDate SortSeq
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

mapper.update:
UPDATE covi_smart4j.sys_dbsync_target
	SET TargetName = #{TargetName}
	, SrcTableNm = #{SrcTableNm}
	, SrcDatabase = #{SrcDatabase}
	, UseYn = #{UseYn}
	, DomainID = #{DomainID}
	, TargetDatabase = #{TargetDatabase}
	, TargetTableNm = #{TargetTableNm}
	, DatasourceSeq = #{DatasourceSeq}
	, Description = #{Description}
	, ModifyDate = now(3)
	, SortSeq = #{SortSeq}
	, ConditionSQL = #{ConditionSQL}
	, ScheduleType = #{ScheduleType}
WHERE TargetSeq = #{TargetSeq}

UPDATE covi_smart4j.sys_dbsync_target
	SET UseYn = #{UseYn}
WHERE TargetSeq = #{TargetSeq}

mapper.delete:
		DELETE FROM covi_smart4j.sys_dbsync_target
		WHERE TargetSeq IN

          #{TargetSeq}

mapper.update:
UPDATE covi_smart4j.sys_dbsync_target
	SET LastSyncTime = now(3)
	, LastStatus = #{LastStatus}
WHERE TargetSeq = #{TargetSeq}
