mapper.select:
SELECT MenuID, DomainID, IsAdmin, MenuType, BizSection, ParentObjectID, ParentObjectType, ServiceDevice, DisplayName, MultiDisplayName, IconClass, IFNULL(MemberOf,0) AS MemberOf, MenuPath, LinkMenuID, SecurityLevel, SortKey, SortPath, SiteMapPosition, HasFolder, IsInherited, IsUse, IsDisplay, URL, MobileURL, Target, MobileTarget, Description, RegisterCode, RegistDate, ModifierCode, ModifyDate, DeleteDate, Reserved1, Reserved2, Reserved3, Reserved4, Reserved5 ,Fn_GetBaseCodeName(0,'MenuType',MenuType) as MenuTypeName ,Fn_GetBaseCodeName(0,'BizSection',BizSection) as BizSectionName FROM covi_smart4j.sys_object_menu WHERE DomainID = #{domainID} AND IsAdmin = #{isAdmin}
AND IsUse = 'Y'
ORDER BY SortPath

		/* queryId : menu.selectTree */
		SELECT a.MenuID AS no, a.DisplayName AS nodeName, a.MemberOf AS pno, a.SortPath, a.DomainID, a.IsAdmin
		  FROM covi_smart4j.sys_object_menu A
     LEFT join covi_smart4j.sys_object_menu B on B.menuid=a.MemberOf AND B.DomainID = A.DomainID
		 WHERE a.DomainID = #{domainID}
		   AND a.IsAdmin = #{isAdmin}

			   AND a.IsUse = #{IsUse}

		   AND (a.MemberOf =0 or b.MenuID IS NOT NULL)
		 ORDER BY a.SortPath

SELECT GROUP_CONCAT(MenuID SEPARATOR ',') AS menuIds,
	   (SELECT MemberOf FROM covi_smart4j.sys_object_menu B WHERE B.MenuID = #{menuId}) AS parentMenuId,
	   (SELECT IF((parentId = tarParentId) AND (tarSortKey > SortKey),'Y','N')
		  FROM (SELECT MemberOf AS parentId, SortKey
		    	 FROM covi_smart4j.sys_object_menu
				WHERE MenuID = #{menuId}
			   ) A,
		 	   (SELECT MemberOf AS tarParentId, SortKey AS tarSortKey
				  FROM covi_smart4j.sys_object_menu
				 WHERE MenuID = #{tarMenuId}
			   ) B
	   ) AS tarSortUpYn
  FROM (SELECT MenuID
	      FROM covi_smart4j.sys_object_menu A
	     WHERE FIND_IN_SET(MenuID, (SELECT CONCAT(GROUP_CONCAT(Level SEPARATOR ','))
  							  		  FROM (SELECT @Ids := (SELECT GROUP_CONCAT(MenuID SEPARATOR ',')
											          		  FROM covi_smart4j.sys_object_menu
											         	     WHERE FIND_IN_SET(MemberOf, @Ids)
	     												   ) Level
									  	      FROM covi_smart4j.sys_object_menu
									  	      JOIN (SELECT @Ids := #{menuId}) temp1
									 	     WHERE FIND_IN_SET(MemberOf, @Ids)
	  									   ) temp2
							 	   )
						  )
	   ) temp3

mapper.update:
UPDATE covi_smart4j.sys_object_menu SET DisplayName=#{displayName}, MultiDisplayName=#{multiDisplayName}, MenuPath = (SELECT CONCAT(IFNULL(MenuPath.MenuPath, ''), #{memberOf}, ';') FROM covi_smart4j.sys_object_menu AS MenuPath WHERE MenuPath.MenuID = #{memberOf}), IsInherited = #{isInherited},
MenuType=#{menuType}, BizSection=#{bizSection}, ServiceDevice=#{serviceDevice}, IconClass=#{iconClass}, SecurityLevel=#{securityLevel}, IsUse=#{isUse}, IsCopy=#{isCopy}, URL=#{url}, MobileURL=#{mobileURL}, Target=#{target}, MobileTarget=#{mobileTarget}, Reserved1=#{reserved1}, Reserved2=#{reserved2}, Reserved5=#{reserved5}, SiteMapPosition=#{siteMapPosition}, Description=#{description},
ModifierCode=#{modID}, ModifyDate=NOW(3) WHERE MenuID = #{menuID}

UPDATE covi_smart4j.sys_object_menu
SET	  IsUse = #{isUse}
	, ModifierCode = #{modID}
	, ModifyDate = NOW(3)
WHERE MenuID = #{menuID}

mapper.delete:
