mapper.select:
SELECT SEQ AS SEQ , SBTF.DESCRIPTION AS DESCRIPTION , SBTF.REGISTDATE AS REGISTDATE , (SELECT DISPLAYNAME FROM SYS_OBJECT_USER WHERE LOGONID = SBTF.REGISTERCODE) AS REGISTERCODE , SBTF.MODIFYDATE AS MODIFYDATE , SBTF.MODIFIERCODE AS MODIFIERCODE , STARTIP AS STARTIP , ENDIP AS ENDIP , ISLOGIN AS ISLOGIN , ISADMIN AS ISADMIN , TWOFACTOR AS TWOFACTOR , FN_BASEGETDICTIONARY_S(#{lang}, SOD.MULTIDISPLAYNAME) AS COMPANYNAME FROM SYS_BASE_TWO_FACTOR AS SBTF LEFT JOIN SYS_OBJECT_DOMAIN AS SOD ON SBTF.DOMAINID = SOD.DOMAINID WHERE TWOFACTOR = 'Y'
AND SBTF.DESCRIPTION LIKE CONCAT('%',#{searchtext},'%')
AND ENDIP LIKE CONCAT('%',#{searchtext},'%')
AND STARTIP LIKE CONCAT('%',#{searchtext},'%')
AND SOD.DOMAINCODE = #{companyCode}
AND SBTF.DOMAINID IN
#{domainID}
STARTIP ENDIP ISLOGIN ISADMIN DESCRIPTION REGISTERCODE MODIFYDATE COMPANYNAME SEQ
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT COUNT(*) FROM ( SELECT SEQ AS SEQ , SBTF.DESCRIPTION AS DESCRIPTION , SBTF.REGISTDATE AS REGISTDATE , (SELECT DISPLAYNAME FROM SYS_OBJECT_USER WHERE LOGONID = SBTF.REGISTERCODE) AS REGISTERCODE , SBTF.MODIFYDATE AS MODIFYDATE , SBTF.MODIFIERCODE AS MODIFIERCODE , STARTIP AS STARTIP , ENDIP AS ENDIP , ISLOGIN AS ISLOGIN , ISADMIN AS ISADMIN , TWOFACTOR AS TWOFACTOR FROM SYS_BASE_TWO_FACTOR AS SBTF LEFT JOIN SYS_OBJECT_DOMAIN AS SOD ON SBTF.DOMAINID = SOD.DOMAINID WHERE TWOFACTOR = 'Y'
AND SBTF.DESCRIPTION LIKE CONCAT('%',#{searchtext},'%')
AND ENDIP LIKE CONCAT('%',#{searchtext},'%')
AND STARTIP LIKE CONCAT('%',#{searchtext},'%')
AND SOD.DOMAINCODE = #{companyCode}
AND SBTF.DOMAINID IN
#{domainID}
)A

mapper.update:
UPDATE SYS_BASE_TWO_FACTOR 
SET  ISLOGIN = #{value}
   , MODIFIERCODE = #{userID}
   , MODIFYDATE = NOW(3)
WHERE SEQ = #{seq}

UPDATE SYS_BASE_TWO_FACTOR 
SET  ISADMIN = #{value}
   , MODIFIERCODE = #{userID}
   , MODIFYDATE = NOW(3)
WHERE SEQ = #{seq}

UPDATE SYS_BASE_TWO_FACTOR 
SET	 TWOFACTOR = 'N'
   , MODIFIERCODE = #{userID}
   , MODIFYDATE = NOW(3)
WHERE SEQ = #{seq}

UPDATE SYS_BASE_TWO_FACTOR 
SET STARTIP = #{startIP}
  , ENDIP = #{endIP}
  , DOMAINID = (SELECT DOMAINID FROM SYS_OBJECT_DOMAIN WHERE DOMAINCODE = #{companyCode})
  , DESCRIPTION = #{Description}
  , ISLOGIN = #{isLogin}
  , ISADMIN = #{isAdmin}
  , MODIFIERCODE = #{userID}
  , MODIFYDATE = now(3)
WHERE 1=1 
AND SEQ = #{seq}

mapper.insert:
INSERT INTO SYS_BASE_TWO_FACTOR (
									STARTIP
								 ,  ENDIP
								 ,  DOMAINID
								 ,  DESCRIPTION
								 ,  ISLOGIN
								 ,  ISADMIN
								 ,  REGISTERCODE
								 ,  REGISTDATE
								 ,  TWOFACTOR
												) VALUES(
									#{startIP}
								 ,  #{endIP}
								 ,  (SELECT DOMAINID FROM SYS_OBJECT_DOMAIN WHERE DOMAINCODE = #{companyCode})
								 ,  #{Description}
								 ,  #{isLogin}
								 ,  #{isAdmin}
								 ,  #{userID}		
								 ,  now(3)
								 ,  'Y'

								 )
