    WHERE 1=1
    AND RegisterCode = #{userCode}
    AND

    	SubscriptionID = #{subscriptionID}


		TargetServiceType = #{targetServiceType}

DELETE FROM SYS_SUBSCRIPTION
WHERE 1=1
AND RegisterCode = #{userCode}
AND TargetServiceType = #{targetServiceType}

mapper.select:
SELECT SS.SubscriptionID AS "SubscriptionID" , SS.TargetServiceType AS "TargetServiceType" , SOF.FolderID AS "FolderID" , FN_FOLDERPATHBYLANGGET_S(#{lang},SOF.FolderPath||SOF.FolderID) AS "FolderPath" , NVL(Fn_BaseGetDictionary_S(#{lang}, SOF.MultiDisplayName), SOF.DisplayName ) AS "FolderName" FROM SYS_SUBSCRIPTION SS INNER JOIN SYS_OBJECT_FOLDER SOF ON (SS.TargetID = SOF.FolderID) LEFT OUTER JOIN board_config BC ON (BC.FolderID = SOF.FolderID) AND SS.TargetServiceType = 'Board' LEFT OUTER JOIN board_config_default BCD ON (BCD.FolderType = SOF.FolderType) WHERE SS.RegisterCode = #{userCode}
AND SS.TargetServiceType = #{targetServiceType}
AND (SS.TargetServiceType != 'Board' OR NVL(BC.UseSubscription, BCD.UseSubscription) = 'Y' ) AND SOF.DeleteDate IS NULL AND SOF.IsUse = 'Y'

