SELECT TodoID AS "TodoID" , Title AS "Title" , URL AS "URL" , Description AS "Description" , IsComplete AS "IsComplete" , ModifyDate AS "ModifyDate" , MessageType AS "MessageType" , TO_CHAR(ModifyDate, 'YYYY.MM.DD')AS "modifyDateText" FROM SYS_TODO WHERE UserCode = #{userCode}
AND TodoID = #{todoId}
ORDER BY ModifyDate DESC

