mapper.select:
/* common.control.comment.selectList */ SELECT * FROM ( SELECT COMMENTID AS "CommentID" ,MEMBEROF AS "MemberOf" ,TARGETSERVICETYPE AS "TargetServiceType" ,TARGETID AS "TargetID" ,COMMENT_ AS "Comment" ,CONTEXT_ AS "Context" ,LIKECOUNT AS "LikeCount" ,REPLYCOUNT AS "ReplyCount"
,'Anonymous' AS "RegisterCode"
,REGISTERCODE AS "RegisterCode"
,TO_CHAR(REGISTDATE, 'YYYY/MM/DD HH24:MI:SS') AS "RegistDate" ,DELETEDATE AS "DeleteDate" ,RESERVED1 AS "Reserved1" ,RESERVED2 AS "Reserved2" ,RESERVED3 AS "Reserved3" ,RESERVED4 AS "Reserved4" ,RESERVED5 AS "Reserved5" ,(SELECT COUNT(L.LikeID) FROM sys_like L WHERE TargetServiceType = 'Comment' AND CommentID = L.TargetID AND L.RegisterCode = #{UserCode}) AS "MyLikeCount" ,(CASE WHEN REGISTERCODE = #{UserCode} THEN 'Y' ELSE 'N' END) AS "AnonymousAuthYn" FROM SYS_COMMENT WHERE (MEMBEROF = #{memberOf}
AND TARGETSERVICETYPE IN ('Board', 'Doc', 'Community')
AND TARGETSERVICETYPE = #{targetServiceType}
AND #{lastCommentID} > COMMENTID
AND TARGETID = #{targetID} AND DELETEDATE IS NULL)
OR (TARGETSERVICETYPE = #{targetServiceType}
AND #{lastCommentID} > COMMENTID
AND TARGETID = #{targetID} AND DELETEDATE IS NOT NULL AND REPLYCOUNT > 0)
ORDER BY COMMENTID DESC ) WHERE 1=1
AND ROWNUM >= 1 AND ROWNUM <= #{rowCount}

mapper.update:


UPDATE SYS_COMMENT
SET CONTEXT_=#{context}
WHERE COMMENTID = #{commentID}



UPDATE SYS_COMMENT
SET REPLYCOUNT = (
	SELECT COUNT(*) FROM SYS_COMMENT
	WHERE memberOf = #{commentID}
	AND DeleteDate IS NULL
)
WHERE COMMENTID = #{commentID}


UPDATE SYS_COMMENT
SET LIKECOUNT=#{likeCount}
WHERE COMMENTID = #{commentID}


        UPDATE SYS_COMMENT
		SET DELETEDATE = SYSDATE
		WHERE COMMENTID = #{commentID}

UPDATE  sys_comment 
SET Comment_ = #{comment}
	, Context_ = #{context}
WHERE CommentID = #{commentID}

mapper.select:
SELECT REGISTERCODE AS "RegisterCode"
FROM SYS_COMMENT
WHERE CommentID = #{memberOf}
