SELECT 'group' AS "itemType" ,gr.COMPANYCODE AS "CompanyCode" ,gr.GROUPCODE AS "GroupCode" ,gr.GROUPTYPE AS "GroupType" ,gr.MULTIDISPLAYNAME AS "GroupName" ,cn.MULTIDISPLAYNAME AS "CompanyName" ,gr.PRIMARYMAIL AS "PrimaryMail" ,gr.MEMBEROF AS "MemberOf" ,gr.GROUPCODE AS AN ,gr.MULTIDISPLAYNAME AS DN ,gr.PRIMARYMAIL AS EM ,cn.COMPANYCODE AS ETID ,cn.MULTIDISPLAYNAME AS ETNM ,gr.MEMBEROF AS SGAN ,gr.MEMBEROF AS SGDN ,gr.RECEIVABLE AS RCV ,gr.SORTPATH AS "SortPath" ,gr.GROUPPATH AS "GroupPath" ,gr.GROUPID AS "GroupID" ,(SELECT COUNT(0) FROM SYS_OBJECT_GROUP sob WHERE sob.MEMBEROF = gr.GROUPCODE AND sob.ISUSE = 'Y') AS "hasChild" ,REPLACE(SYS_GETFULLPATH(gr.GROUPCODE, #{lang}),'@','>') AS "GroupFullPath" FROM SYS_OBJECT_GROUP gr LEFT JOIN SYS_OBJECT_GROUP cn ON gr.COMPANYCODE = cn.GROUPCODE AND cn.GROUPTYPE = 'Company'
AND gr.ISUSE = 'Y' AND gr.ISDISPLAY = 'Y' AND (gr.MEMBEROF != 'NOUSE' OR gr.MEMBEROF is null) AND (gr.MEMBEROF != 'NOUNIT' OR gr.MEMBEROF is null) AND gr.GROUPTYPE in ('Dept','Company')
AND gr.COMPANYCODE =#{companyCode}
AND gr.MemberOf = #{searchText}
AND gr.DISPLAYNAME LIKE '%'||#{searchText}||'%'
gr.SORTPATH ASC

SELECT 'group' AS "itemType" ,gr.COMPANYCODE AS "CompanyCode" ,gr.GROUPCODE AS "GroupCode" ,gr.GROUPTYPE AS "GroupType" ,gr.MULTIDISPLAYNAME AS "GroupName" ,cn.MULTIDISPLAYNAME AS "CompanyName" ,gr.PRIMARYMAIL AS "PrimaryMail" ,gr.MEMBEROF AS "MemberOf" ,gr.GROUPCODE AS AN ,gr.MULTIDISPLAYNAME AS DN ,gr.PRIMARYMAIL AS EM ,cn.COMPANYCODE AS ETID ,cn.MULTIDISPLAYNAME AS ETNM ,gr.MEMBEROF AS SGAN ,gr.MEMBEROF AS SGDN ,gr.RECEIVABLE AS RCV ,gr.SORTPATH AS "SortPath" ,gr.GROUPPATH AS "GroupPath" ,gr.GROUPID AS "GroupID" ,(SELECT COUNT(0) FROM SYS_OBJECT_GROUP sob WHERE sob.MEMBEROF = gr.GROUPCODE AND sob.ISUSE = 'Y') AS "hasChild" , REPLACE(SYS_GETFULLPATH(gr.GROUPCODE, #{lang}),'@','>') AS "GroupFullPath" FROM SYS_OBJECT_GROUP gr LEFT JOIN SYS_OBJECT_GROUP cn ON gr.COMPANYCODE = cn.GROUPCODE AND cn.GROUPTYPE = 'Company'
AND gr.ISUSE = 'Y' AND gr.ISDISPLAY = 'Y' AND (gr.MEMBEROF != 'NOUSE' OR gr.MEMBEROF is null) AND (gr.MEMBEROF != 'NOUNIT' OR gr.MEMBEROF is null) AND gr.GROUPTYPE != 'Dept'
AND gr.COMPANYCODE =#{companyCode}
AND gr.DISPLAYNAME LIKE '%'||#{searchText}||'%'
AND (REGEXP_LIKE(gr.GroupPath, (SELECT LISTAGG(';'||GroupCode, ';|') WITHIN GROUP(ORDER BY GroupCode) FROM SYS_OBJECT_GROUP WHERE Reserved5 IN
#{item}
)) OR gr.GroupType = 'Company')
AND ( (REGEXP_LIKE(gr.GroupPath, '_Community'||';') AND gr.GroupCode = (SELECT CU_Code FROM COMMUNITY WHERE CU_ID = #{communityId}) OR gr.Reserved5 = 'Community') OR (NOT REGEXP_LIKE(gr.GroupPath, '_Community'||';')) )
gr.SORTPATH ASC

SELECT GroupID AS "GroupID" , GroupCode AS "GroupCode" , MultiDisplayName AS "MultiDisplayName" FROM SYS_OBJECT_GROUP WHERE IsUse = 'Y' AND IsDisplay = 'Y' AND GroupType = 'Company'
AND GroupCode IN
#{item}
AND GroupCode = #{companyCode}
ORDER BY SortKey

SELECT 'group' AS "itemType" ,gr.CompanyCode AS "CompanyCode" ,gr.GroupCode AS "GroupCode" ,gr.GroupType AS "GroupType" ,gr.MultiDisplayName AS "GroupName" ,cn.MultiDisplayName AS "CompanyName" ,gr.PrimaryMail AS "PrimaryMail" ,gr.MemberOf AS "MemberOf" ,gr.GroupCode AS "AN" ,gr.MultiDisplayName AS "DN" ,gr.PrimaryMail AS "EM" ,cn.CompanyCode AS "ETID" ,cn.MultiDisplayName AS "ETNM" ,gr.MemberOf AS "SGAN" ,gr.MemberOf AS "SGDN" ,gr.Receivable AS "RCV" ,gr.SortPath AS "SortPath" ,gr.GroupPath AS "GroupPath" ,gr.GroupID AS "GroupID" ,(SELECT COUNT(0) FROM sys_object_group self WHERE self.MemberOf =gr.GroupCode AND self.IsUse = 'Y') AS "hasChild" , REPLACE(SYS_GETFULLPATH(gr.GROUPCODE, #{lang}),'@','>') AS "GroupFullPath" , gr.Approvable AS "Approvable" , gr.Receivable AS "Receivable" , gr.ISMAIL AS "IsMail" FROM sys_object_group gr LEFT JOIN sys_object_group cn ON gr.CompanyCode = cn.GroupCode AND cn.GroupType = 'Company'
AND gr.IsUse = 'Y' AND gr.IsDisplay = 'Y' AND (gr.MemberOf != 'NOUSE' OR gr.MemberOf is null) AND (gr.MemberOf != 'NOUNIT' OR gr.MemberOf is null)
AND gr.GroupType IN ('Dept','Company')
AND gr.GroupType != 'Dept'
AND (REGEXP_LIKE(gr.GroupPath, (SELECT LISTAGG(';'||GroupCode, ';|') WITHIN GROUP(ORDER BY GroupCode) FROM SYS_OBJECT_GROUP WHERE Reserved5 IN
#{item}
)) OR gr.GroupType = 'Company')
and gr.GroupType NOT IN ('Authority','Community') and SUBSTR(gr.GroupCode, INSTR(gr.GroupCode, '_') + 1) NOT IN ('Authority','Community')
AND (gr.MemberOf = #{companyCode} OR gr.GroupCode = #{companyCode}
OR #{defaultValueGroupPath} LIKE '%;'||gr.MemberOf||';%'
OR #{userGroupPath} LIKE '%;'||gr.MemberOf||';%'
)
AND gr.CompanyCode =#{companyCode}
AND gr.DisplayName LIKE '%||#{searchText}||'%'
gr.SortPath asc

SELECT CompanyCode AS "CompanyCode", GroupCode AS "GroupCode", GroupPath AS "GroupPath", MultiDisplayName AS "MultiDisplayName" FROM sys_object_group WHERE GroupCode IN
#{item}
ORDER BY SortPath ASC

SELECT * FROM ( SELECT A.OUCODE , A.OUCODE AS "AN" , A.OUORDER , A.UCORGFULLNAME , A.OU , A.OU AS "DN" , A.TOPOUCODE , A.REPOUCODE , A.PARENTOUCODE , NVL(A.PARENTOUNAME, '') AS PARENTOUNAME , A.OULEVEL , A.ISUSE , OUReceiveDocumentYN AS USEYN , A.UCCHIEFTITLE , A.OUSTEP , ( SELECT CASE WHEN A.OUCODE = A.REPOUCODE THEN A.UCCHIEFTITLE ELSE CASE WHEN NVL(A.UCCHIEFTITLE,' ') = ' ' THEN Z.UCCHIEFTITLE ELSE (Fn_GetParentOUName_ReceiveY(Z.OUCODE) || '(' || A.UCCHIEFTITLE || ')') END END FROM JWF_PROCESSGOV_LDAP Z WHERE (Z.OUCODE = A.PARENTOUCODE AND A.PARENTOUCODE != '0000000') UNION ALL SELECT CASE WHEN A.OUCODE = A.REPOUCODE THEN A.UCCHIEFTITLE ELSE CASE WHEN NVL(A.UCCHIEFTITLE,' ') = ' ' THEN Z.UCCHIEFTITLE ELSE (Fn_GetParentOUName_ReceiveY(Z.OUCODE) || '(' || A.UCCHIEFTITLE || ')') END END FROM JWF_PROCESSGOV_LDAP Z WHERE (Z.OUCODE = A.OUCODE AND A.PARENTOUCODE = '0000000') ) AS DISPLAY_UCCHIEFTITLE , A.SUBCOUNT AS HASSUBOU FROM JWF_PROCESSGOV_LDAP A WHERE 1=1
AND A.OUSTEP LIKE '%' || #{searchText} || '%' AND OUReceiveDocumentYN = 'Y' ) TEMP WHERE NVL(TEMP.DISPLAY_UCCHIEFTITLE, ' ') != ' ' ORDER BY TEMP.OULEVEL ASC, TEMP.OU ASC
AND A.PARENTOUCODE = #{parentOUCode}
AND A.PARENTOUCODE = #{memberOf}
AND NVL(A.OULEVEL, ' ') != ' ' AND A.ISUSE IS NULL AND A.UCCHIEFTITLE IS NOT NULL ORDER BY OU ) TEMP

SELECT * FROM ( SELECT ORGCD, CMPNYNM, SENDERNM, BIZNO, ADRES, DELETEFLAG, '0' AS HASSUBOU, '0000000' AS PARENTOUCODE, ORGCD AS AN, CMPNYNM AS DN, CMPNYNM AS OUSTEP, CMPNYNM || '(' || SENDERNM || ')' AS DISPLAY_UCCHIEFTITLE FROM JWF_PROCESSGOV_GOV24 WHERE DELETEFLAG = 'N' ) A WHERE 1=1
AND A.CMPNYNM LIKE '%' || #{searchText} || '%'
AND A.PARENTOUCODE = #{parentOUCode}
AND A.PARENTOUCODE = #{memberOf}
ORDER BY CMPNYNM ASC
