mapper.select:
SELECT COUNT(*) FROM log_connect WHERE LogonID = #{userCode}
AND LogonDate >= TO_DATE(#{startDate},'YYYY-MM-DD HH24:MI:SS')
AND LogonDate <= TO_DATE(#{endDate},'YYYY-MM-DD HH24:MI:SS')

/* queryID : license.manage.selectLicenseNameCnt */
SELECT NVL(COUNT(0), 0) AS "cnt"
FROM (
	SELECT LicName
	FROM sys_license
	WHERE 1=1
	AND LicName = #{licName}
) RESULT

mapper.insert:
/* queryID : license.manage.addLicenseInfo */
INSERT INTO sys_license
(	
	LICNAME
	,LicModule
	, ISOPT
	, INITPORTAL
	, DESCRIPTION
	, REGISTERCODE
	, REGISTDATE
) VALUES (
	#{licName}
	, #{licModule}
	, #{isOpt}
	, #{portalID}
	, #{description}
	, #{userCode}
	, SYSDATE
)

	SELECT SYS_LICENSE_SEQ.CURRVAL FROM DUAL

mapper.update:
/* queryID : license.manage.addLicenseBizSection */

	INTO sys_license_bizsection
	(
		  LICSEQ
		, BIZSECTION
		, REGISTERCODE
		, REGISTDATE
	) VALUES 
	(
		  #{licSeq}
		, #{module}
		, #{userCode}
		, SYSDATE
	)

/* queryID : license.manage.editLicenseInfo */
UPDATE sys_license
SET	  LicName = #{licName}
	, LicModule = #{licModule}
	, IsOpt = #{isOpt}


	, InitPortal = #{portalID}


	, InitPortal = null


	, IsMbPortal = #{isMbPortal}
	, Description = #{description}
	, ModifyerCode = #{userCode}
	, ModifyDate = SYSDATE
WHERE LicSeq = #{licSeq}

/* queryID : license.manage.deleteLicense */
UPDATE sys_license
SET	  IsUse = 'N'
	, ModifyerCode = #{userCode}
	, ModifyDate = SYSDATE
WHERE 1=1
AND LicSeq IN

	#{licSeq}

mapper.delete:
/* queryID : license.manage.deleteLicenseBizSection */
DELETE FROM sys_license_bizsection
WHERE LicSeq = #{licSeq}
