mapper.select:
SELECT Code AS "Code" , SsoType AS "SsoType" , SortKey AS "SortKey" , Lable AS "Lable" , SettingValue AS "SettingValue" , IsUse AS "IsUse" , Description AS "Description" , RegisterCode AS "RegisterCode" , RegisterDate AS "RegisterDate" , ModifierCode AS "ModifierCode" , ModifierDate AS "ModifierDate" FROM SSO_CONFIG sso WHERE 1=1 AND SsoType = #{ssotype} AND DomainID = (SELECT NVL(MAX(DomainID), 0) FROM SSO_CONFIG WHERE Code = sso.Code AND DomainID = #{domainID})
AND ROWNUM BETWEEN #{pageSize} AND #{pageOffset}
SortKey ASC

mapper.update:

UPDATE
	SSO_CONFIG
SET
	IsUse = #{IsUse},
	ModifierCode = #{ModID}
	WHERE Code = #{Code}

mapper.select:
SELECT Code AS "Code" , SsoType AS "SsoType" , SortKey AS "SortKey" , Lable AS "Lable" , SettingValue AS "SettingValue" , IsUse AS "IsUse" , Description AS "Description" , RegisterCode AS "RegisterCode" , RegisterDate AS "RegisterDate" , ModifierCode AS "ModifierCode" , ModifierDate AS "ModifierDate" FROM SSO_CONFIG sso WHERE 1=1
AND SsoAuthType = #{SsoAuthType}
AND DomainID = (SELECT NVL(MAX(DomainID), 0) FROM SSO_CONFIG WHERE Code = sso.Code AND DomainID = #{domainID}) ORDER BY SortKey ASC

mapper.update:
 	    UPDATE SSO_CONFIG

					 SettingValue = #{SettingValue}

		WHERE 1=1 
		AND Code = #{Code}


UPDATE oauth_client 
SET client_name = #{client_name}
	, redirect_uri = #{redirect_uri}
WHERE userCode = #{ur_code}

