mapper.update:
UPDATE sys_access_url
SET URL = #{url}
  , IsUse = #{isUse}
  , Description = #{description}
  , ModifierCode = #{modifierCode}
  , ModifyDate = SYSDATE
WHERE AccessUrlID = #{accessURLID}

UPDATE sys_access_url
SET  IsUse = #{isUse}
  , ModifierCode = #{modifierCode}
  , ModifyDate = SYSDATE
WHERE AccessUrlID = #{accessURLID}

mapper.delete:
	    DELETE FROM sys_access_url
	    WHERE AccessUrlID IN 

          #{accessURLID}
