mapper.select:
/* queryId : menu.selectgrid */
SELECT MenuID AS "MenuID" , DomainID AS "DomainID" , IsAdmin AS "IsAdmin" , MenuType AS "MenuType" , BizSection AS "BizSection" , ParentObjectID AS "ParentObjectID" , ParentObjectType AS "ParentObjectType" , ServiceDevice AS "ServiceDevice" , DisplayName AS "DisplayName" , MultiDisplayName AS "MultiDisplayName" , IconClass AS "IconClass" , NVL(MemberOf,0) AS "MemberOf" , MenuPath AS "MenuPath" , LinkMenuID AS "LinkMenuID" , SecurityLevel AS "SecurityLevel" , SortKey AS "SortKey" , SortPath AS "SortPath" , SiteMapPosition AS "SiteMapPosition" , HasFolder AS "HasFolder" , IsInherited AS "IsInherited" , IsUse AS "IsUse" , IsDisplay AS "IsDisplay" , URL AS "URL" , MobileURL AS "MobileURL" , Target AS "Target" , MobileTarget AS "MobileTarget" , Description AS "Description" , RegisterCode AS "RegisterCode" , TO_CHAR(RegistDate, 'YYYY-MM-DD HH24:MI:SS') AS "RegistDate" , ModifierCode AS "ModifierCode" , TO_CHAR(ModifyDate, 'YYYY-MM-DD HH24:MI:SS') AS "ModifyDate" , DeleteDate AS "DeleteDate" , Reserved1 AS "Reserved1" , Reserved2 AS "Reserved2" , Reserved3 AS "Reserved3" , Reserved4 AS "Reserved4" , Reserved5 AS "Reserved5" , Fn_GetBaseCodeName(0,'MenuType',MenuType) as "MenuTypeName" , Fn_GetBaseCodeName(0,'BizSection',BizSection) as "BizSectionName" FROM SYS_OBJECT_MENU WHERE DomainID = #{domainID} AND IsAdmin = #{isAdmin}
AND IsUse = 'Y'
ORDER BY SortPath

/* queryId : menu.selectTree */ SELECT A.MenuID AS "no" , A.DisplayName AS "nodeName" , A.MemberOf AS "pno" , A.SortPath AS "SortPath" , A.DomainID AS "DomainID" , A.IsAdmin AS "IsAdmin" FROM SYS_OBJECT_MENU A LEFT JOIN SYS_OBJECT_MENU B ON B.MenuId = A.MemberOf AND B.DomainID = A.DomainID WHERE A.DomainID = #{domainID} AND A.IsAdmin = #{isAdmin}
AND A.IsUse = #{IsUse}
AND (A.MemberOf = 0 OR B.MenuID IS NOT NULL) ORDER BY A.SortPath

SELECT LISTAGG(MenuID, ',') WITHIN GROUP (ORDER BY MenuID) AS "menuIds"
  ,(SELECT MemberOf FROM sys_object_menu B WHERE B.MenuID = #{menuId}) AS "parentMenuId"
  ,(SELECT CASE WHEN parentId = tarParentID AND (tarSortKey > SortKey) THEN 'Y' ELSE 'N' END
    FROM (SELECT MemberOf AS parentId, SortKey
	    	 FROM sys_object_menu
			WHERE MenuID = #{menuId}
		   ) A,
	 	   (SELECT MemberOf AS tarParentId, SortKey AS tarSortKey
			  FROM sys_object_menu
			 WHERE MenuID = #{tarMenuId} 
		   ) B
  ) AS "tarSortUpYn"
FROM (
  SELECT MenuID
    ,(SELECT P.MemberOf FROM sys_object_menu P WHERE P.MenuID = #{menuId}) AS ParentMenuID
    FROM sys_object_menu
    WHERE 1=1
    CONNECT BY PRIOR MenuID = MemberOf
    START WITH MenuID = #{menuId}
) childmenu
GROUP BY ParentMenuID

mapper.update:
UPDATE SYS_OBJECT_MENU SET DisplayName=#{displayName}, MultiDisplayName=#{multiDisplayName}, MenuPath = (SELECT NVL(MenuPath.MenuPath, '') || #{memberOf} || ';' FROM SYS_OBJECT_MENU MenuPath WHERE MenuPath.MenuID = #{memberOf}), IsInherited = #{isInherited},
MenuType=#{menuType}, BizSection=#{bizSection}, ServiceDevice=#{serviceDevice}, IconClass=#{iconClass}, SecurityLevel=#{securityLevel}, IsUse=#{isUse}, IsCopy=#{isCopy}, URL=#{url}, MobileURL=#{mobileURL}, Target=#{target}, MobileTarget=#{mobileTarget}, Reserved1=#{reserved1}, Reserved2=#{reserved2}, Reserved5=#{reserved5}, SiteMapPosition=#{siteMapPosition}, Description=#{description},
ModifierCode=#{modID}, ModifyDate=SYSDATE WHERE MenuID = #{menuID}

UPDATE SYS_OBJECT_MENU
SET	  IsUse = #{isUse}
	, ModifierCode = #{modID}
	, ModifyDate = SYSDATE
WHERE MenuID = #{menuID}

mapper.delete:
        DELETE FROM SYS_OBJECT_MENU
		WHERE MenuID IN 

			#{item}

mapper.insert:
INSERT INTO SYS_OBJECT_MENU ( DomainID , IsAdmin , MenuType , BizSection , ParentObjectID , ParentObjectType , ServiceDevice , DisplayName , MultiDisplayName , IconClass , MemberOf , MenuPath , LinkMenuID , SecurityLevel , SortKey , SortPath , SiteMapPosition , HasFolder , IsInherited , IsUse , IsDisplay , URL , MobileURL , Target , MobileTarget , Description , RegisterCode , RegistDate , ModifierCode , ModifyDate , DeleteDate , Reserved1 , Reserved2 , Reserved3 , Reserved4 , Reserved5 , OriginMenuID , IsCopy ) SELECT * FROM (
SELECT #{item.domainID} AS DomainID , IsAdmin , MenuType , BizSection , 1 , ParentObjectType , ServiceDevice , DisplayName , MultiDisplayName , IconClass , 0 , MenuPath , LinkMenuID , SecurityLevel , #{item.sortKey} AS SortKey , #{item.sortPath} AS SortPath , SiteMapPosition , HasFolder , IsInherited , IsUse , IsDisplay , URL , MobileURL , Target , MobileTarget , Description , #{userCode} AS RegisterCode , SYSDATE AS RegistDate , #{userCode} AS ModifierCode , SYSDATE AS ModifyDate , DeleteDate , Reserved1 , Reserved2 , Reserved3 , Reserved4 , Reserved5 , MenuID AS OriginMenuID , IsCopy FROM SYS_OBJECT_MENU WHERE DomainID = 0 AND MenuID = #{item.menuId}
) A

mapper.update:
MERGE INTO SYS_OBJECT_MENU trg USING ( SELECT trg.MenuID AS parentMenuId , org.MenuID AS MenuID FROM SYS_OBJECT_MENU org JOIN SYS_OBJECT_MENU trg ON org.ParentObjectID = trg.OriginMenuID WHERE org.DomainID = 0 AND trg.DomainID = #{domainID} AND org.MenuID IN
#{menuID}
) a ON (trg.OriginMenuID = a.MenuID AND trg.Menutype != 'Top' AND trg.DomainID = #{domainID}) WHEN MATCHED THEN UPDATE SET ParentObjectID = a.parentMenuId , MemberOf = a.parentMenuId

mapper.insert:
{
	CALL
	DECLARE
	BEGIN
		-- 메뉴 권한 생성-사용자 권한-관리조직
		INSERT INTO SYS_OBJECT_ACL (ObjectID, ObjectType, SubjectCode, SubjectType, AclList, Security, Create_, Delete_, Modify_, Execute, View_, Read, Description, RegisterCode, RegistDate)
		(
			SELECT o.MenuID, 'MN', (#{domainID}||'_'||REPLACE(a.SubjectCode, '0_', '')), a.SubjectType, a.AclList, a.Security, a.Create_, a.Delete_, a.Modify_, a.Execute, a.View_, a.Read, '', #{userCode}, SYSDATE
			FROM SYS_OBJECT_MENU o
			INNER JOIN SYS_OBJECT_MENU m ON o.OriginMenuID = m.MenuID AND m.DomainID = 0
			INNER JOIN SYS_OBJECT_ACL a ON m.MenuID = a.ObjectID
			INNER JOIN SYS_OBJECT_GROUP g ON a.SubjectCode = g.GroupCode AND g.GroupType = 'Manage'
			WHERE o.DomainID = #{domainID}
			AND o.IsAdmin = 'N'
			AND o.IsUse = 'Y'
			AND a.ObjectType = 'MN'
			AND a.SubjectType = 'GR'
			AND m.MenuID IN

				#{menuID}

		);

		-- 메뉴 권한 생성-사용자 권한-설정 제외
		INSERT INTO SYS_OBJECT_ACL (ObjectID, ObjectType, SubjectCode, SubjectType, AclList, Security, Create_, Delete_, Modify_, Execute, View_, Read, Description, RegisterCode, RegistDate)
		(
			SELECT m.MenuID, 'MN', d.DomainCode, 'CM', 'SCDMEVR', 'S', 'C', 'D', 'M', 'E', 'V', 'R', '', #{userCode}, SYSDATE
			FROM SYS_OBJECT_MENU m
			INNER JOIN SYS_OBJECT_DOMAIN d ON d.DomainID = m.DomainID
			LEFT JOIN SYS_OBJECT_ACL a ON m.MenuID = a.ObjectID AND a.ObjectType = 'MN'
			WHERE m.DomainID = #{domainID}
			AND m.IsAdmin = 'N'
			AND a.AclID IS NULL
			AND m.BizSection != 'Conf'
			AND m.OriginMenuID IN

				#{menuID}

		);

		-- 메뉴 권한 생성-시스템 권한
		INSERT INTO SYS_OBJECT_ACL (ObjectID, ObjectType, SubjectCode, SubjectType, AclList, Security, Create_, Delete_, Modify_, Execute, View_, Read, Description, RegisterCode, RegistDate)
		(
			SELECT MenuID, 'MN', (DomainID||'_Admin'), 'GR', 'SCDMEVR', 'S', 'C', 'D', 'M', 'E', 'V', 'R', '', #{userCode}, SYSDATE
			FROM SYS_OBJECT_MENU
			WHERE DomainID = #{domainID}
			AND (IsAdmin = 'Y' or  BizSection = 'Conf')
			AND OriginMenuID IN

				#{menuID}

		);
	END
}

mapper.select:
/* queryId : menu.selectChildCount */
SELECT 	NVL(MAX(SortKey), -1)+1
FROM 	SYS_OBJECT_MENU
WHERE	DomainID = #{tarDomainId}
AND 	IsAdmin = #{tarIsAdmin}
AND 	MemberOf = #{tarMenuId}

SELECT A.MENUID AS "MenuID", A.DISPLAYNAME AS "DisplayName", A.SORTPATH AS "SortPath" FROM SYS_OBJECT_MENU A WHERE 1=1 AND A.DOMAINID = #{ExportDomainId}
AND A.ISADMIN = #{IsAdmin}
AND A.ISUSE = 'Y'
AND ( 1 = CASE WHEN (SELECT MEMBEROF FROM SYS_OBJECT_MENU WHERE MENUID = #{menuId}) = 0 AND A.MENUPATH LIKE #{menuId}||';%' THEN 1 WHEN (SELECT MEMBEROF FROM SYS_OBJECT_MENU WHERE MENUID = #{menuId}) != 0 AND A.MENUPATH LIKE '%;'||#{menuId} THEN 1 END OR (A.MENUID = #{menuId}) )
AND A.MENUID NOT IN(SELECT B.ORIGINMENUID FROM SYS_OBJECT_MENU B WHERE B.DOMAINID = #{ImportDomainId} AND B.ORIGINMENUID IS NOT NULL)
