mapper.insert:
INSERT INTO sys_search_word (
	SearchWord
	, SearchCnt
	 , SearchDate 
	 , RecentlyPoint 
	, DomainID
	, System
	 , CreateDate 
) VALUES (
	#{SearchWord}
	, #{SearchCnt}
	 , TO_DATE(#{SearchDate}, 'YYYY-MM-DD')
	 , #{RecentlyPoint} 
	, #{DomainID}
	, #{System}
	 , TO_DATE(#{CreateDate}, 'YYYY-MM-DD')
)

mapper.update:
UPDATE sys_search_word
SET 
	SearchWord = #{SearchWord}
	, SearchCnt = #{SearchCnt}
	, SearchDate = TO_DATE(#{SearchDate}, 'YYYY-MM-DD')
	, RecentlyPoint = #{RecentlyPoint}
	, DomainID = #{DomainID}
	, System = #{System}
WHERE
	SearchWordID = #{SearchWordID}

