mapper.select:
/* userlock.getUserLock */ SELECT Fn_BaseGetDictionary_S(#{lang}, u.MultiDisplayName) AS "DisplayName" , Fn_BaseGetDictionary_S(#{lang}, bg.MultiDeptName) AS "DeptName" , (SELECT Fn_BaseGetDictionary_S(#{lang}, ug.MultiDisplayName) FROM sys_object_group g JOIN sys_object_group ug ON ug.GroupType ='Dept' AND ug.GroupCode = g.MemberOf WHERE g.GroupType ='Dept' AND g.GroupCode = bg.DeptCode) AS "UpDeptName" , Fn_BaseGetDictionary_S(#{lang}, bg.MultiJobPositionName) AS "JobPositionName" , u.UserCode AS "UserCode" , u.login_fail_count AS "login_fail_count" , TO_CHAR(password_change_date,'YYYY-MM-DD HH24:MI:SS') AS "password_change_date" , latest_login_date AS "latest_login_date" , bg.DeptCode AS "DeptCode" FROM sys_object_user u JOIN sys_object_user_basegroup bg ON u.UserCode = bg.UserCode AND JobType = 'Origin' JOIN sys_object_domain d on bg.Companycode = d.domainCode WHERE u.IsUse ='Y' AND u.IsDisplay='Y' AND u.ACCOUT_LOCK='Y' AND d.DomainID = #{DomainID}
AND u.MultiDisplayName like '%'||#{searchText}||'%'
DisplayName DeptName JobPositionName UserCode login_fail_count password_change_date latest_login_date
ASC DESC

	SELECT count(h.HistoryID)
	  FROM sys_object_user_lock_history h
     WHERE h.UserCode =#{UserCode}
