mapper.select:
/* webhardCommon.box.selectBox */ SELECT SEQ , UUID , DOMAIN_ID , DOMAIN_CODE , OWNER_TYPE , OWNER_ID , OWNER_NAME , BOX_NAME , BOX_PATH , BOX_MAX_SIZE , BOX_CURRENT_SIZE , USE_YN , BLOCK_YN , IFNULL(BLOCK_REASON, '') AS BLOCK_REASON , CREATED_DATE , IFNULL(UPDATED_DATE, '') AS UPDATED_DATE , IFNULL(DELETED_DATE, '') AS DELETED_DATE FROM covi_webhard4j.WH_BOX_LIST WHERE 1=1
AND UUID = #{boxUuid}
AND DOMAIN_ID = #{domainId}
AND OWNER_TYPE = #{ownerType}
AND OWNER_ID = #{ownerId}

mapper.update:
/* webhardCommon.box.updateBox */
UPDATE covi_webhard4j.WH_BOX_LIST 
SET  BOX_CURRENT_SIZE = BOX_CURRENT_SIZE + CAST(#{fileSize} AS SIGNED INTEGER)

	, USE_YN = #{useYn}


	, BLOCK_YN = #{blockYn}
	, BLOCK_REASON = #{blockReason}

, UPDATED_DATE = NOW(3)

	, DELETED_DATE = NOW(3)

WHERE UUID = #{boxUuid}

mapper.insert:
/* webhardCommon.box.insertBox */
INSERT INTO covi_webhard4j.WH_BOX_LIST (
	  UUID
	, DOMAIN_ID
	, DOMAIN_CODE
	, OWNER_TYPE
	, OWNER_ID
	, OWNER_NAME
	, BOX_NAME
	, BOX_MAX_SIZE
	, BOX_CURRENT_SIZE
	, USE_YN
	, BLOCK_YN
	, BLOCK_REASON
	, BOX_PATH
	, CREATED_DATE
) VALUES (
	  #{boxUuid}
	, #{domainId}
	, #{domainCode}
	, #{ownerType}
	, #{ownerId}
	, #{ownerName}
	, #{boxName}
	, COALESCE((SELECT BASIC_BOX_VOLUME FROM covi_webhard4j.WH_CONFIG WHERE DOMAIN_ID = #{domainId}), 10240)
	, 0
	, 'Y'
	, 'N'
	, ''
	, #{boxPath}
	, NOW()
)
