mapper.select:
/* webhardAdmin.boxManage.selectBoxListCount */ SELECT COUNT(*) FROM WH_BOX_LIST
AND DOMAIN_ID = #{domainID}
AND UPPER(OWNER_NAME) LIKE UPPER('%' || TRIM(#{searchWord}) || '%')
AND UPPER(OWNER_ID) LIKE UPPER('%' || TRIM(#{searchWord}) || '%')
AND ( UPPER(OWNER_NAME) LIKE UPPER('%' || TRIM(#{searchWord}) || '%') OR UPPER(OWNER_ID) LIKE UPPER('%' || TRIM(#{searchWord}) || '%') )

mapper.update:
/* webhardAdmin.boxManage.setBoxConfig */
UPDATE WH_BOX_LIST
SET	  BOX_MAX_SIZE = #{totalQuota}
	, UPDATED_DATE = SYSDATE
WHERE OWNER_ID = #{ownerID}

/* webhardAdmin.boxManage.blockBox */
UPDATE WH_BOX_LIST
SET	  BLOCK_YN = 'Y'
	, BLOCK_REASON = #{blockReason}
	, UPDATED_DATE = SYSDATE
WHERE UUID IN 

	#{item}

mapper.select:
SELECT BLOCK_REASON
FROM WH_BOX_LIST 
WHERE UUID = #{boxUuid}

mapper.update:
/* webhardAdmin.boxManage.setBoxBlockYn */
UPDATE WH_BOX_LIST
SET	  BLOCK_YN = #{blockYn}
	, BLOCK_REASON = #{blockReason}
	, UPDATED_DATE = SYSDATE
WHERE UUID = #{boxUuid}

/* webhardAdmin.boxManage.setBoxUseYn */
UPDATE WH_BOX_LIST
SET	  USE_YN = #{useYn}
	, UPDATED_DATE = SYSDATE
WHERE UUID = #{boxUuid}
