mapper.select:
SELECT f.FileID , f.StorageID , f.ServiceType , f.ObjectID , f.ObjectType , f.MessageID , f.Version , f.SaveType , f.LastSeq , f.Seq , f.FilePath , f.FileName , f.SavedName , f.Extention , f.Size , f.ThumWidth , f.ThumHeight , f.Description , f.RegisterCode , f.RegistDate AS RegistDate , f.CompanyCode , s.LastSeq as "StorageLastSeq" , s.FilePath as "StorageFilePath" , s.InlinePath , s.IsActive FROM covi_smart4j.sys_file f JOIN covi_smart4j.sys_storage s on s.StorageID = f.StorageID WHERE f.ServiceType = #{ServiceType} AND f.ObjectID = #{ObjectID} AND f.MessageID = #{MessageID} AND f.SaveType != 'INLINE'
AND IFNULL(f.IsDeleted, 'N') = 'Y'
AND IFNULL(f.IsDeleted, 'N') = 'N'
AND f.ObjectType = #{ObjectType}
AND f.Version = #{Version}

