    	update covi_smart4j.sys_storage 
   		set   
    		DomainID = #{domainID}
			, ServiceType = #{serviceType}
			, LastSeq = #{lastSeq}
			, FilePath = #{filePath}
			, InlinePath = #{inlinePath}
			, IsActive = #{isActive}
			, Description = #{description}
			, Reserved1 = #{reserved1}
			, Reserved2 = #{reserved2}
			, Reserved3 = #{reserved3}
			, Reserved4 = #{reserved4}
			, Reserved5 = #{reserved5}
		where
			StorageID = #{storageID}

    	update covi_smart4j.sys_storage 
   		set IsActive = CASE WHEN IsActive = 'Y' THEN 'N' ELSE 'Y' END
		where StorageID = #{storageID}

