AND s.DomainID = #{domain}
s.DomainID ServiceType LastSeq FilePath InlinePath s.IsActive RegistDate
ASC DESC
s.IsActive DESC, s.RegistDate DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

