mapper.select:

SELECT GroupPath
FROM covi_smart4j.sys_object_group
WHERE GroupCode = #{deptCode}

SELECT AclID , ObjectID , A.ObjectType , AclList , ( CASE WHEN SubjectType = 'CM' THEN '9' WHEN SubjectType = 'DEPT' THEN '2' ELSE '3' END ) AS AclPriority , F.ObjectType AS BizSection FROM covi_smart4j.sys_object_acl AS a INNER JOIN covi_smart4j.sys_object_group AS g ON a.SubjectCode = g.GroupCode INNER JOIN covi_smart4j.sys_object_group_type AS gt ON gt.GroupType = g.GroupType INNER JOIN covi_smart4j.sys_object_folder F ON A.ObjectID = F.FolderID WHERE a.ObjectType = 'FD'
AND F.ObjectType = #{serviceType}
ORDER BY AclPriority DESC, Priority DESC, GroupPath ASC, AclID DESC

SELECT a.MenuID, a.DomainID, a.IsAdmin, a.MenuType, a.BizSection, a.ParentObjectID, a.ParentObjectType, a.ServiceDevice, a.MultiDisplayName, a.IconClass, a.MemberOf, a.MenuPath, a.LinkMenuID, a.SecurityLevel, a.SortKey, a.SiteMapPosition, a.SortPath, a.HasFolder, a.IsInherited, a.IsUse, a.IsDisplay, case when b.MenuID IS NOT NULL AND a.OriginMenuID IS not null AND a.OriginMenuID>0 then b.URL ELSE a.URL end URL, case when b.MenuID IS NOT NULL AND a.OriginMenuID IS not null AND a.OriginMenuID>0 then b.MobileURL ELSE a.MobileURL end MobileURL, case when b.MenuID IS NOT NULL AND a.OriginMenuID IS not null AND a.OriginMenuID>0 then b.Reserved5 ELSE a.Reserved5 end Reserved5, a.Target, a.MobileTarget, a.Reserved1, a.Reserved2, a.Reserved3, a.Reserved4, case when a.domainid =0 || a.OriginMenuID IS null then a.menuid else a.OriginMenuID end OriginMenuID FROM covi_smart4j.sys_object_menu a LEFT JOIN covi_smart4j.sys_object_menu b ON a.OriginMenuID = b.MenuID AND b.DomainID = 0 INNER JOIN sys_object_user ui on ui.UserCode = #{userID} WHERE a.MenuID IN
#{item}
AND a.IsUse = 'Y' AND a.IsDisplay = 'Y'
AND a.BizSection IN
#{item}
ORDER BY a.DomainID ASC,a.SortKey ASC

