mapper.select:
SELECT UserCode, (@rownum:=@rownum+1) AS SortSeq FROM ( SELECT ub.UserCode FROM covi_smart4j.sys_object_user u JOIN covi_smart4j.sys_object_user_basegroup AS ub ON u.UserCode = ub.UserCode AND ub.JobType = 'Origin'
JOIN covi_smart4j.sys_object_domain AS d ON ub.CompanyCode = d.DomainCode AND d.DomainID = #{domainID}
JOIN covi_smart4j.sys_license_opt_user AS e ON u.UserCode = e.UserCode AND e.LicSeq = #{licSeq}
JOIN covi_smart4j.sys_object_domain_license AS e ON u.LicSeq = e.LicSeq AND e.LicSeq = #{licSeq}
AND d.DomainID = e.DomainID
AND e.DomainID = 0
JOIN (SELECT @rownum:=0) TMP WHERE u.IsUse = 'Y' ORDER BY u.SortKey+0, u.DisplayName ) a

SELECT SortSeq FROM ( SELECT UserCode, (@rownum:=@rownum+1) AS SortSeq FROM ( SELECT ub.UserCode FROM covi_smart4j.sys_object_user u JOIN covi_smart4j.sys_object_user_basegroup AS ub ON u.UserCode = ub.UserCode AND ub.JobType = 'Origin'
JOIN covi_smart4j.sys_object_domain AS d ON ub.CompanyCode = d.DomainCode AND d.DomainID = #{domainID}
JOIN covi_smart4j.sys_license_opt_user AS e ON u.UserCode = e.UserCode AND e.LicSeq = #{licSeq}
JOIN covi_smart4j.sys_object_domain_license AS e ON u.LicSeq = e.LicSeq
AND d.DomainID = e.DomainID
AND e.DomainID = 0
JOIN (SELECT @rownum:=0) TMP WHERE u.IsUse = 'Y' ORDER BY u.SortKey+0, u.DisplayName ) a ) sortTable WHERE UserCode = #{userCode}

/* queryID : framework.license.selectLicenseUserList */ SELECT e.* FROM ( SELECT a.DomainId, d.DomainCode, d.DisplayName, a.LicSeq, b.LicName , b.IsOpt, a.ServiceUser, a.ServiceStart, a.ServiceEnd, a.ExtraServiceUser , a.ExtraExpiredate , IFNULL(case when b.IsOpt = 'Y' then lico.LicUsingCnt else lic.LicUsingCnt END, 0) LicUsingCnt , a.LicUserCount, a.LicExpireDate, a.LicExUserCount, a.LicEx1Date , IFNULL(b.Description, '') AS Description FROM sys_object_domain d JOIN sys_object_domain_license a ON d.DomainId = a.DomainId JOIN sys_license b ON a.LicSeq = b.LicSeq LEFT JOIN ( SELECT LicSeq, COUNT(ou.Usercode) LicUsingCnt, od.DomainId FROM sys_object_user ou JOIN sys_object_user_basegroup oub ON ou.UserCode = oub.UserCode AND oub.JobType='Origin'
JOIN sys_object_domain od ON oub.CompanyCode = od.DomainCode AND od.DomainId = #{domainID}
WHERE ou.IsUse = 'Y' GROUP BY LicSeq, od.DomainId) lic ON A.LicSeq = lic.LicSeq AND b.IsOpt = 'N' AND A.DomainId = lic.DomainID LEFT JOIN ( SELECT lou.LicSeq, COUNT(ou.Usercode) LicUsingCnt, lou.DomainId FROM sys_object_user ou JOIN sys_license_opt_user lou ON ou.UserCode = lou.UserCode WHERE ou.IsUse = 'Y'
AND lou.DomainId= #{domainID}
GROUP BY lou.LicSeq, lou.DomainId) lico ON A.LicSeq = lico.LicSeq AND b.IsOpt = 'Y' AND A.DomainId = lico.DomainID WHERE 1=1
AND a.DomainId= #{domainID}
AND DisplayName LIKE CONCAT('%',#{selectsearch},'%')
and now() between a.ServiceStart AND a.ServiceEnd
) e
UNION ALL (SELECT t1.DomainId , t1.DomainCode , t1.DisplayName , t2.LicSeq , '-' AS LicName , '-' AS IsOpt , IFNULL(t2.ServiceUser, 0) AS ServiceUser , t2.ServiceStart , t2.ServiceEnd , IFNULL(t2.ExtraServiceUser, 0) AS ExtraServiceUser , t2.ExtraExpiredate , 0 AS LicUsingCnt , IFNULL(t2.LicUserCount, "") AS LicUserCount , t2.LicExpireDate , IFNULL(t2.LicExUserCount, "") AS LicExUserCount , t2.LicEx1Date , '-' AS Description FROM sys_object_domain t1 LEFT JOIN sys_object_domain_license t2 on t2.domainid = t1.domainid WHERE t2.LicSeq is null
AND t1.DisplayName LIKE CONCAT('%',#{selectsearch},'%')
)
ORDER BY DomainId, LicSeq
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT LicSeq
FROM   sys_license_opt_user
WHERE  DomainID = #{domainID}
AND    UserCode = #{userCode}

/* queryID : framework.license.selectLicenseUserCnt */ SELECT COUNT(1) FROM ( SELECT 1 FROM sys_object_domain_license a INNER JOIN sys_license b ON a.LicSeq = b.LicSeq INNER JOIN sys_object_domain c ON c.DomainID = a.DomainID WHERE 1=1
AND a.DomainId= #{domainID}
AND DisplayName LIKE CONCAT('%',#{selectsearch},'%')
AND NOW() BETWEEN a.ServiceStart AND a.ServiceEnd
UNION ALL SELECT 1 FROM sys_object_domain t1 LEFT JOIN sys_object_domain_license t2 ON t2.domainid = t1.domainid WHERE t2.LicSeq IS NULL
AND t1.DisplayName LIKE CONCAT('%',#{selectsearch},'%')
) x

/* queryID : framework.license.selectLicenseUserInfoCnt */
SELECT
			COUNT(1)
FROM 		sys_object_user A
INNER JOIN 	sys_object_user_basegroup B ON B.UserCode = A.UserCode AND JobType = 'Origin'


INNER JOIN 	sys_object_domain C ON B.CompanyCode = C.DomainCode AND C.DomainID = #{domainId}


INNER JOIN 	sys_license_opt_user D ON A.UserCode = D.UserCode AND D.LicSeq = #{licSeq} AND C.DomainID = D.DomainID


INNER JOIN 	sys_object_domain_license D ON A.LicSeq = D.LicSeq AND D.LicSeq = #{licSeq} AND D.DomainID = #{domainId}






INNER JOIN 	sys_license_opt_user D ON A.UserCode = D.UserCode AND D.LicSeq = #{licSeq}


INNER JOIN 	sys_object_domain_license D ON A.LicSeq = D.LicSeq AND D.LicSeq = #{licSeq} AND D.DomainID = 0




WHERE A.IsUse = 'Y'



AND			UPPER(B.MultiDeptName) LIKE CONCAT('%', UPPER(#{searchText}), '%')


AND			UPPER(A.MultiDisplayName) LIKE CONCAT('%', UPPER(#{searchText}),'%')

/* queryID : framework.license.selectLicenseUserInfoList */ SELECT (@rownum:=@rownum+1) AS RNUM, T2.* FROM ( SELECT Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS UserName , A.LogonID AS LogonID , Fn_BaseGetDictionary_S(#{lang}, B.MultiDeptName) AS DeptName , A.IsUse AS IsUse , A.SortKey , A.DisplayName , B.CompanyCode , A.UserCode , D.LicSeq , E.DomainID FROM sys_object_user A INNER JOIN sys_object_user_basegroup B ON B.UserCode = A.UserCode AND JobType = 'Origin' LEFT JOIN sys_object_domain E ON E.DomainCode = B.CompanyCode
INNER JOIN sys_object_domain C ON B.CompanyCode = C.DomainCode AND C.DomainID = #{domainId}
INNER JOIN sys_license_opt_user D ON A.UserCode = D.UserCode AND D.LicSeq = #{licSeq} AND C.DomainID = D.DomainID
INNER JOIN sys_object_domain_license D ON A.LicSeq = D.LicSeq AND D.LicSeq = #{licSeq} AND D.DomainID = #{domainId}
INNER JOIN sys_license_opt_user D ON A.UserCode = D.UserCode AND D.LicSeq = #{licSeq}
INNER JOIN sys_object_domain_license D ON A.LicSeq = D.LicSeq AND D.LicSeq = #{licSeq} AND D.DomainID = 0
JOIN (SELECT @rownum:=0) TMP WHERE A.IsUse = 'Y' ORDER BY A.SortKey+0, A.DisplayName ) T2 WHERE 1=1
AND UPPER(DeptName) LIKE CONCAT('%', UPPER(#{searchText}), '%')
AND UPPER(UserName) LIKE CONCAT('%', UPPER(#{searchText}),'%')
UserName LogonID DeptName IsUse RNUM
DESC ASC
LIMIT #{pageSize} OFFSET #{pageOffset}

/* queryID : framework.license.selectLicenseAddUserCnt */ SELECT COUNT(1) FROM sys_object_user A INNER JOIN sys_object_user_basegroup B on B.JobType = 'Origin' AND B.UserCode = A.UserCode INNER JOIN sys_object_domain C ON C.DomainCode = B.CompanyCode LEFT JOIN sys_license_opt_user D ON D.UserCode = A.UserCode AND D.LicSeq = #{licSeq} WHERE C.DomainID = #{domainId} AND a.IsUse ='Y' AND a.IsDisplay ='Y' AND D.LicSeq is null
AND UPPER(B.MultiDeptName) LIKE CONCAT('%', UPPER(#{searchText}), '%')
AND UPPER(A.MultiDisplayName) LIKE CONCAT('%', UPPER(#{searchText}),'%')

/* queryID : framework.license.selectLicenseAddUserList */ SELECT Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS UserName , A.LogonID AS LogonID , Fn_BaseGetDictionary_S(#{lang}, B.MultiDeptName) AS DeptName , A.IsUse AS IsUse , A.UserCode FROM sys_object_user A INNER JOIN sys_object_user_basegroup B ON B.JobType = 'Origin' AND B.UserCode = A.UserCode AND A.IsUse = 'Y' INNER JOIN sys_object_domain C ON C.DomainCode = B.CompanyCode LEFT JOIN sys_license_opt_user D ON D.UserCode = A.UserCode AND D.LicSeq = #{licSeq} WHERE C.DomainID = #{domainId} AND a.IsUse ='Y' AND a.IsDisplay ='Y' AND D.LicSeq is null
AND UPPER(B.MultiDeptName) LIKE CONCAT('%', UPPER(#{searchText}), '%')
AND UPPER(A.MultiDisplayName) LIKE CONCAT('%', UPPER(#{searchText}),'%')
ORDER BY a.SortKey, UserName, DeptName ASC
LIMIT #{pageSize} OFFSET #{pageOffset}

mapper.delete:
/* queryID : framework.license.deleteUserLicense */
DELETE FROM sys_license_opt_user
WHERE 	1=1
AND 	LicSeq = #{LicSeq}
AND 	DomainID = #{DomainID}
AND  	UserCode = #{UserCode}

mapper.select:
/* queryID : framework.license.selectDownloadExcel1st */ SELECT CONCAT(T.DisplayName, ' / ', T.DomainCode) AS DisplayName , T.IsOpt , T.LicName , T.ServiceEnd , T.ServiceUser , T.ExtraExpiredate , T.ExtraServiceUser , T.LicUsingCnt , (T.TempRemainCnt - T.LicUsingCnt) AS RemainCnt , CASE WHEN (T.TempOverYN - T.LicUsingCnt) < 0 then 'Y' else '' END OverYN FROM ( SELECT a.DomainId , d.DomainCode, d.DisplayName, b.IsOpt, b.LicName , a.LicSeq, a.ServiceEnd, a.ServiceUser, a.ExtraExpiredate, a.ExtraServiceUser , CASE WHEN b.IsOpt = 'Y' THEN lico.LicUsingCnt ELSE lic.LicUsingCnt END LicUsingCnt , CASE WHEN DATE_FORMAT(NOW(), '%Y%m%d') > a.ExtraExpiredate THEN IFNULL(a.ServiceUser, 0) ELSE IFNULL(a.ServiceUser, 0 ) + IFNULL(a.ExtraServiceUser, 0) END TempRemainCnt , CASE WHEN DATE_FORMAT(NOW(), '%Y%m%d') > a.ExtraExpiredate THEN a.ServiceUser ELSE (a.ServiceUser + IFNULL(a.ExtraServiceUser, 0)) END TempOverYN FROM sys_object_domain d JOIN sys_object_domain_license a ON d.DomainId = a.DomainId JOIN sys_license b ON a.LicSeq = b.LicSeq LEFT JOIN ( SELECT od.DomainId, LicSeq, COUNT(ou.Usercode) LicUsingCnt FROM sys_object_user ou JOIN sys_object_user_basegroup oub ON ou.UserCode = oub.UserCode AND oub.JobType='Origin' JOIN sys_object_domain od ON oub.CompanyCode = od.DomainCode WHERE ou.IsUse = 'Y'
AND od.DomainId = #{domainID}
GROUP BY od.DomainId, LicSeq) lic ON A.DomainId = lic.DomainID and A.LicSeq = lic.LicSeq AND b.IsOpt = 'N' LEFT JOIN ( SELECT lou.DomainId, lou.LicSeq, COUNT(ou.Usercode) LicUsingCnt FROM sys_object_user ou JOIN sys_license_opt_user lou ON ou.UserCode = lou.UserCode WHERE ou.IsUse = 'Y'
AND lou.DomainId= #{domainID}
GROUP BY lou.DomainId, lou.LicSeq) lico ON A.DomainId = lico.DomainID AND A.LicSeq = lico.LicSeq AND b.IsOpt = 'Y' WHERE 1=1
AND a.DomainId= #{domainID}
AND DisplayName LIKE CONCAT('%',#{selectsearch},'%')
and now() between a.ServiceStart AND a.ServiceEnd
) T ORDER BY T.DomainId, T.LicSeq

