mapper.select:
   	SELECT MailAddress
		FROM sys_object_user
		WHERE LogonID = #{id}

   	SELECT LanguageCode
		FROM sys_object_user
		WHERE LogonID = #{id}

mapper.update:
UPDATE SYS_OBJECT_USER  SET 
	ACCOUT_LOCK = #{ACCOUT_LOCK}
WHERE LogonID = #{id}
AND IsUse = 'Y'

UPDATE covi_smart4j.log_connect
SET LogoutDate = now(3)
WHERE LogonID = #{logonID}
AND IPAddress = #{IPAddress}
AND OS = #{OS}
AND Browser = #{browser}
ORDER BY LogonDate DESC
LIMIT 1

mapper.select:
SELECT GROUP_CONCAT(BC.CODE SEPARATOR '|') FROM sys_base_code bc LEFT JOIN ( SELECT slb.bizsection BizSection FROM sys_object_user SOU JOIN sys_license_bizsection slb ON slb.LicSeq = SOU.LicSeq WHERE SOU.UserCode = #{userCode}
UNION SELECT slb.bizsection FROM sys_license_opt_user slop JOIN sys_license_bizsection slb ON slop.LICSEQ = slb.LicSeq WHERE slop.usercode= #{userCode} AND slop.domainid = #{DomainID} AND slop.LICSEQ IN
#{item}
) A ON bc.Code = a.BizSection WHERE bc.DomainID = 0 AND bc.CodeGroup = 'BizSection' AND bc.IsUse = 'Y' AND (IFNULL(NULLIF(bc.Reserved3,''),'N') = 'N' OR (bc.Reserved3 = 'Y' AND A.BizSection IS NOT NULL) OR (bc.Reserved3 = 'M' AND #{isManage} = 'Y' ))
