mapper.select:
 		SELECT u.UserCode AS "UserCode"
			 , ROW_NUMBER() OVER( ORDER BY u.SortKey+1, u.DisplayName ) AS "SortSeq" 
		FROM 	SYS_OBJECT_USER u
		JOIN    sys_object_user_basegroup ub ON u.UserCode = ub.UserCode AND ub.JobType = 'Origin'

		JOIN 	sys_object_domain d ON ub.CompanyCode = d.DomainCode AND d.DomainID = #{domainID}



			JOIN sys_license_opt_user e      ON u.UserCode = e.UserCode   AND e.LicSeq = #{licSeq}


			JOIN sys_object_domain_license e ON u.LicSeq   = e.LicSeq   AND e.LicSeq = #{licSeq}



				AND d.DomainID = e.DomainID


				AND e.DomainID = 0


		WHERE u.IsUse = 'Y'

/* queryId : framework.license.selectLicenseUserList */
SELECT E.DomainId AS "DomainId" , E.DomainCode AS "DomainCode" , E.DisplayName AS "DisplayName" , E.LicSeq AS "LicSeq" , E.LicName AS "LicName" , E.IsOpt AS "IsOpt" , E.ServiceUser AS "ServiceUser" , E.ServiceStart AS "ServiceStart" , E.ServiceEnd AS "ServiceEnd" , E.ExtraServiceUser AS "ExtraServiceUser" , E.ExtraExpiredate AS "ExtraExpiredate" , E.LicUsingCnt AS "LicUsingCnt" , E.LicUserCount AS "LicUserCount" , E.LicExpireDate AS "LicExpireDate" , E.LicExUserCount AS "LicExUserCount" , E.LicEx1Date AS "LicEx1Date" , E.Description AS "Description" FROM ( SELECT A.DomainId , D.DomainCode , D.DisplayName , A.LicSeq , B.LicName , B.IsOpt , A.ServiceUser , A.ServiceStart , A.ServiceEnd , A.ExtraServiceUser , A.ExtraExpiredate , NVL(CASE WHEN B.IsOpt = 'Y' THEN LICO.LicUsingCnt ELSE LIC.LicUsingCnt END, 0) AS LicUsingCnt , A.LicUserCount , A.LicExpireDate , A.LicExUserCount , A.LicEx1Date , NVL(b.Description, '') AS Description FROM SYS_OBJECT_DOMAIN D JOIN SYS_OBJECT_DOMAIN_LICENSE A ON D.DomainId = A.DomainId JOIN SYS_LICENSE B ON A.LicSeq = B.LicSeq LEFT JOIN (SELECT LicSeq, COUNT(ou.Usercode) LicUsingCnt, od.DomainId FROM sys_object_user ou JOIN sys_object_user_basegroup oub ON ou.UserCode = oub.UserCode AND oub.JobType='Origin'
JOIN sys_object_domain od ON oub.CompanyCode = od.DomainCode AND od.DomainId = #{domainID}
WHERE ou.IsUse = 'Y' GROUP BY LicSeq, od.DomainId) lic ON A.LicSeq = lic.LicSeq AND b.IsOpt = 'N' AND A.DomainId = lic.DomainID LEFT JOIN (SELECT lou.LicSeq, COUNT(ou.Usercode) LicUsingCnt, lou.DomainId FROM sys_object_user ou JOIN sys_license_opt_user lou ON ou.UserCode = lou.UserCode WHERE ou.IsUse = 'Y'
AND lou.DomainId= #{domainID}
GROUP BY lou.LicSeq, lou.DomainId) lico ON A.LicSeq = lico.LicSeq AND b.IsOpt = 'Y' AND A.DomainId = lico.DomainID WHERE 1=1
AND A.DomainId= #{domainID}
AND DisplayName LIKE '%'||#{selectsearch}||'%'
AND SYSDATE BETWEEN A.ServiceStart AND A.ServiceEnd
) E
UNION ALL (SELECT T1.DomainId AS "DomainId" , T1.DomainCode AS "DomainCode" , T1.DisplayName AS "DisplayName" , T2.LicSeq AS "LicSeq" , '-' AS "LicName" , '-' AS "IsOpt" , NVL(T2.ServiceUser, 0) AS "ServiceUser" , T2.ServiceStart AS "ServiceStart" , T2.ServiceEnd AS "ServiceEnd" , NVL(T2.ExtraServiceUser, 0) AS "ExtraServiceUser" , T2.ExtraExpiredate AS "ExtraExpiredate" , 0 AS "LicUsingCnt" , NVL(T2.LicUserCount, '') AS "LicUserCount" , T2.LicExpireDate AS "LicExpireDate" , NVL(T2.LicExUserCount, '') AS "LicExUserCount" , T2.LicEx1Date AS "LicEx1Date" , '-' AS "Description" FROM SYS_OBJECT_DOMAIN T1 LEFT JOIN SYS_OBJECT_DOMAIN_LICENSE T2 ON T2.domainid = T1.domainid WHERE T2.LicSeq IS NULL
AND T1.DisplayName LIKE CONCAT('%',#{selectsearch},'%')
)
ORDER BY "DomainId", "LicSeq" ASC

SELECT LicSeq
FROM   sys_license_opt_user
WHERE  DomainID = #{domainID}
AND    UserCode = #{userCode}

/* queryID : framework.license.selectLicenseUserCnt */ SELECT COUNT(1) FROM ( SELECT 1
FROM SYS_OBJECT_DOMAIN_LICENSE A INNER JOIN SYS_LICENSE B ON A.LicSeq = B.LicSeq INNER JOIN SYS_OBJECT_DOMAIN C ON C.DomainID = A.DomainID WHERE 1=1
AND A.DomainId= #{domainID}
AND DisplayName LIKE '%'||#{selectsearch}||'%'
AND SYSDATE BETWEEN A.ServiceStart AND A.ServiceEnd
UNION ALL SELECT 1 FROM SYS_OBJECT_DOMAIN T1 LEFT JOIN SYS_OBJECT_DOMAIN_LICENSE T2 ON T2.domainid = T1.domainid WHERE T2.LicSeq IS NULL
AND T1.DisplayName LIKE '%'||#{selectsearch}||'%'
) X

/* queryID : framework.license.selectLicenseUserInfoCnt */
SELECT
			COUNT(1)
FROM 		sys_object_user A
INNER JOIN 	sys_object_user_basegroup B ON B.UserCode = A.UserCode AND JobType = 'Origin'


INNER JOIN 	sys_object_domain C ON B.CompanyCode = C.DomainCode AND C.DomainID = #{domainId}


INNER JOIN 	sys_license_opt_user D ON A.UserCode = D.UserCode AND D.LicSeq = #{licSeq} AND C.DomainID = D.DomainID


INNER JOIN 	sys_object_domain_license D ON A.LicSeq = D.LicSeq AND D.LicSeq = #{licSeq} AND D.DomainID = #{domainId}






INNER JOIN 	sys_license_opt_user D ON A.UserCode = D.UserCode AND D.LicSeq = #{licSeq} 


INNER JOIN 	sys_object_domain_license D ON A.LicSeq = D.LicSeq AND D.LicSeq = #{licSeq} AND D.DomainID = 0




WHERE A.IsUse = 'Y'



AND			UPPER(B.MultiDeptName) LIKE '%'||UPPER(#{searchText})||'%'


AND			UPPER(A.MultiDisplayName) LIKE '%'||UPPER(#{searchText})||'%'

/* queryID : framework.license.selectLicenseUserInfoList */
SELECT T2.UserName AS "UserName" , T2.LogonID AS "LogonID" , T2.DeptName AS "DeptName" , T2.IsUse AS "IsUse" , T2.SortKey AS "SortKey" , T2.DisplayName AS "DisplayName" , T2.CompanyCode AS "CompanyCode" , T2.UserCode AS "UserCode" , T2.LicSeq AS "LicSeq" , T2.DomainID AS "DomainID" FROM ( SELECT Fn_BaseGetDictionary_S(#{lang}, A.MultiDisplayName) AS UserName , A.LogonID AS LogonID , Fn_BaseGetDictionary_S(#{lang}, B.MultiDeptName) AS DeptName , A.IsUse AS IsUse , A.SortKey , A.DisplayName , B.CompanyCode , A.UserCode , D.LicSeq , E.DomainID FROM SYS_OBJECT_USER A INNER JOIN SYS_OBJECT_USER_BASEGROUP B ON B.UserCode = A.UserCode AND JobType = 'Origin' LEFT JOIN SYS_OBJECT_DOMAIN E ON E.DOMAINCODE = B.COMPANYCODE
INNER JOIN SYS_OBJECT_DOMAIN C ON B.CompanyCode = C.DomainCode AND C.DomainID = #{domainId}
INNER JOIN SYS_LICENSE_OPT_USER D ON A.UserCode = D.UserCode AND D.LicSeq = #{licSeq} AND C.DomainID = D.DomainID
INNER JOIN SYS_OBJECT_DOMAIN_LICENSE D ON A.LicSeq = D.LicSeq AND D.LicSeq = #{licSeq} AND D.DomainID = #{domainId}
INNER JOIN SYS_LICENSE_OPT_USER D ON A.UserCode = D.UserCode AND D.LicSeq = #{licSeq}
INNER JOIN SYS_OBJECT_DOMAIN_LICENSE D ON A.LicSeq = D.LicSeq AND D.LicSeq = #{licSeq} AND D.DomainID = 0
WHERE A.IsUse = 'Y' ORDER BY A.SortKey+0, A.DisplayName ) T2 WHERE 1=1
AND UPPER(DeptName) LIKE '%'||UPPER(#{searchText})||'%'
AND UPPER(UserName) LIKE '%'||UPPER(#{searchText})||'%'
UserName LogonID DeptName IsUse DisplayName
DESC ASC

/* queryID : framework.license.selectDownloadExcel1st */ SELECT CONCAT(CONCAT(T.DisplayName, ' / '), T.DomainCode) AS "DisplayName" , T.IsOpt AS "IsOpt" , T.LicName AS "LicName" , T.ServiceEnd AS "ServiceEnd" , T.ServiceUser AS "ServiceUser" , T.ExtraExpiredate AS "ExtraExpiredate" , T.ExtraServiceUser AS "ExtraServiceUser" , T.LicUsingCnt AS "LicUsingCnt" , (T.TempRemainCnt - T.LicUsingCnt) AS "RemainCnt" , CASE WHEN (T.TempOverYN - T.LicUsingCnt) < 0 then 'Y' else '' END OverYN FROM ( SELECT a.DomainId, d.DomainCode , d.DisplayName , b.IsOpt , b.LicName , a.LicSeq , a.ServiceEnd , a.ServiceUser , a.ExtraExpiredate , a.ExtraServiceUser , CASE WHEN b.IsOpt = 'Y' THEN lico.LicUsingCnt ELSE lic.LicUsingCnt END LicUsingCnt , CASE WHEN TO_DATE(SYSDATE, 'YYYY-MM-DD HH24:mi:SS') > a.ExtraExpiredate THEN NVL(a.ServiceUser, 0) ELSE NVL(a.ServiceUser, 0 ) + NVL(a.ExtraServiceUser, 0) END TempRemainCnt , CASE WHEN TO_DATE(SYSDATE, 'YYYY-MM-DD HH24:mi:SS') > a.ExtraExpiredate THEN a.ServiceUser ELSE (a.ServiceUser + NVL(a.ExtraServiceUser, 0)) END TempOverYN FROM sys_object_domain d JOIN sys_object_domain_license a ON d.DomainId = a.DomainId JOIN sys_license b ON a.LicSeq = b.LicSeq LEFT JOIN ( SELECT od.DomainId, LicSeq, COUNT(ou.Usercode) LicUsingCnt FROM sys_object_user ou JOIN sys_object_user_basegroup oub ON ou.UserCode = oub.UserCode AND oub.JobType='Origin' JOIN sys_object_domain od ON oub.CompanyCode = od.DomainCode WHERE ou.IsUse = 'Y'
AND od.DomainId = #{domainID}
GROUP BY od.DomainId, LicSeq) lic ON A.DomainId = lic.DomainID and A.LicSeq = lic.LicSeq AND b.IsOpt = 'N' LEFT JOIN ( SELECT lou.DomainId, lou.LicSeq, COUNT(ou.Usercode) LicUsingCnt FROM sys_object_user ou JOIN sys_license_opt_user lou ON ou.UserCode = lou.UserCode WHERE ou.IsUse = 'Y'
AND lou.DomainId= #{domainID}
GROUP BY lou.DomainId, lou.LicSeq) lico ON A.DomainId = lico.DomainID AND A.LicSeq = lico.LicSeq AND b.IsOpt = 'Y' WHERE 1=1
AND a.DomainId= #{domainID}
AND DisplayName LIKE '%'||#{selectsearch}||'%'
and SYSDATE between a.ServiceStart AND a.ServiceEnd
) T ORDER BY T.DomainId, T.LicSeq

/* queryID : framework.license.selectLicenseAddUserCnt */ SELECT COUNT(1) FROM SYS_OBJECT_USER A INNER JOIN SYS_OBJECT_USER_BASEGROUP B ON B.JobType = 'Origin' AND B.UserCode = A.UserCode INNER JOIN SYS_OBJECT_DOMAIN C ON C.DomainCode = B.CompanyCode LEFT JOIN SYS_LICENSE_OPT_USER D ON D.UserCode = A.UserCode AND D.LicSeq = #{licSeq} WHERE C.DomainID = #{domainId} AND A.IsUse ='Y' AND A.IsDisplay ='Y' AND D.LicSeq is null
AND UPPER(B.MultiDeptName) LIKE '%'||UPPER(#{searchText})||'%'
AND UPPER(A.MultiDisplayName) LIKE '%'||UPPER(#{searchText})||'%'

mapper.delete:
/* queryID : framework.license.deleteUserLicense */
DELETE FROM SYS_LICENSE_OPT_USER
WHERE 	1=1
AND 	LicSeq = #{LicSeq}
AND 	DomainID = #{DomainID}
AND  	UserCode = #{UserCode}

