mapper.select:
/* queryID = framework.FileUtil.selectAttachAll */ SELECT f.FileID AS "FileID" , f.StorageID AS "StorageID" , f.ServiceType AS "ServiceType" , f.ObjectID AS "ObjectID" , f.ObjectType AS "ObjectType" , f.MessageID AS "MessageID" , f.Version AS "Version" , f.SaveType AS "SaveType" , f.LastSeq AS "LastSeq" , f.Seq AS "Seq" , f.FilePath AS "FilePath" , f.FileName AS "FileName" , f.SavedName AS "SavedName" , f.Extention AS "Extention" , f.Size_ AS "Size" , f.ThumWidth AS "ThumWidth" , f.ThumHeight AS "ThumHeight" , f.Description AS "Description" , f.RegisterCode AS "RegisterCode" , TO_CHAR(f.RegistDate, 'YYYY-MM-DD HH24:MI:SS') AS "RegistDate" , f.CompanyCode AS "CompanyCode" , s.LastSeq as "StorageLastSeq" , s.FilePath as "StorageFilePath" , s.InlinePath AS "InlinePath" , s.IsActive AS "IsActive" FROM SYS_FILE f JOIN sys_storage s on s.StorageID = f.StorageID WHERE f.ServiceType = #{ServiceType}
AND NVL(f.IsDeleted, 'N') = 'Y'
AND NVL(f.IsDeleted, 'N') = 'N'
AND f.ObjectID = #{ObjectID}
AND f.MessageID = #{MessageID} AND f.SaveType != 'INLINE'
AND f.ObjectType = #{ObjectType}
AND f.Version = #{Version}

SELECT F.FILEID AS "FileID" ,F.STORAGEID AS "StorageID" ,F.SERVICETYPE AS "ServiceType" ,F.OBJECTID AS "ObjectID" ,F.OBJECTTYPE AS "ObjectType" ,F.MESSAGEID AS "MessageID" ,F.VERSION AS "Version" ,F.SAVETYPE AS "SaveType" ,F.LASTSEQ AS "LastSeq" ,F.SEQ AS "Seq" ,F.FILEPATH AS "FilePath" ,F.FILENAME AS "FileName" ,F.SAVEDNAME AS "SavedName" ,F.EXTENTION AS "Extention" ,F.SIZE_ AS "Size" ,F.THUMWIDTH AS "ThumWidth" ,F.THUMHEIGHT AS "ThumHeight" ,F.DESCRIPTION AS "Description" ,TO_CHAR(F.REGISTDATE, 'YYYY-MM-DD HH24:MI:SS') AS "RegistDate" ,F.REGISTERCODE AS "RegisterCode" ,F.CompanyCode AS "CompanyCode" ,S.LastSeq AS "StorageLastSeq" ,S.FilePath AS "StorageFilePath" ,S.InlinePath AS "InlinePath" ,S.IsActive AS "IsActive" FROM SYS_FILE F INNER JOIN sys_storage S ON S.StorageID = F.StorageID WHERE F.SaveType != 'INLINE' AND NVL(F.IsDeleted, 'N') = 'N'
AND F.MessageID = #{MessageID}
AND F.ObjectID = #{ObjectID}
AND F.ObjectType = #{ObjectType}
AND F.ServiceType = #{ServiceType}

