mapper.select:
SELECT a.PortalID , a.PortalType , h.CodeName AS PortalTypeName , a.BizSection , i.CodeName AS BizSectionName , a.SortKey , a.DisplayName , a.IsUse , a.CompanyCode , DATE_FORMAT(a.RegistDate, '%Y-%m-%d %H:%i:%s') as RegistDate , b.DisplayName as LayoutName , c.DisplayName as RegisterName , IFNULL(( SELECT CASE d.SubjectType WHEN 'CM' THEN e.MultiDisplayName WHEN 'GR' THEN e.MultiDisplayName WHEN 'UR' THEN f.MultiDisplayName END FROM sys_object_acl AS d LEFT JOIN sys_object_group AS e ON (d.SubjectType = 'GR' OR d.SubjectType = 'CM') AND d.SubjectCode = e.GroupCode LEFT JOIN sys_object_user AS f ON d.SubjectType = 'UR' AND d.SubjectCode = f.UserCode WHERE ObjectID = a.PortalID AND ObjectType = 'PT' GROUP BY d.SubjectType, e.DisplayName, f.DisplayName LIMIT 1 ), '') as aclDisplayName , IFNULL((SELECT IFNULL(COUNT(*), 1) - 1 FROM sys_object_acl as g WHERE ObjectID = a.PortalID AND ObjectType = 'PT' GROUP BY g.ObjectID), 0) as aclDisplayCount FROM portal AS a LEFT JOIN portal_layout AS b ON a.LayoutID = b.LayoutID LEFT JOIN sys_object_user AS c ON a.RegisterCode = c.UserCode LEFT JOIN sys_object_domain AS d ON d.DomainCode = a.CompanyCode LEFT JOIN sys_base_code AS h ON h.CodeGroup = 'PortalType' AND a.PortalType = h.Code LEFT JOIN sys_base_code AS i ON i.CodeGroup = 'bizSection' AND a.BizSection = i.Code
AND a.BizSection != 'Community' AND h.DomainID = (SELECT DomainID FROM covi_smart4j.sys_base_code WHERE Code = h.Code AND CodeGroup = 'PortalType' ORDER BY IF(DomainID = d.DomainID, 0, 1) LIMIT 1 ) AND i.DomainID = (SELECT DomainID FROM covi_smart4j.sys_base_code WHERE Code = i.Code AND CodeGroup = 'bizSection' ORDER BY IF(DomainID = d.DomainID, 0, 1) LIMIT 1 )
AND a.DisplayName LIKE CONCAT('%',#{searchWord},'%')
AND b.DisplayName LIKE CONCAT('%',#{searchWord},'%')
AND c.DisplayName LIKE CONCAT('%',#{searchWord},'%')
AND CompanyCode = #{companyCode}
AND PortalType = #{portalType}
PortalTypeName BizSectionName SortKey DisplayName LayoutName RegisterName IsUse RegistDate PortalID
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}


SELECT PortalID, LayoutID, CompanyCode, DisplayName, MultiDisplayName, PortalType, ThemeCode, SortKey, URL, Description, BizSection, PortalTag, LayoutSizeTag
FROM covi_smart4j.portal
WHERE PortalID = #{portalID}

