		, #{companyCode}
		, #{portalName}
		, #{dicPortalName}
		, #{portalType}
		, #{themeCode}
		, #{sortKey}
		, #{url}
		, 'Y'
		, #{description}
		, #{userCode}
		, now(3)
		, (SELECT LayoutTag FROM covi_smart4j.portal_layout where layoutID = #{layoutID}) 
		, #{bizSection});

            SELECT LAST_INSERT_ID();

