mapper.select:
/* attend.common.getSubDeptList */ SELECT GroupID ,GroupCode ,CompanyCode ,GroupType ,MemberOf ,DisplayName ,MultiDisPlayName ,Fn_BaseGetDictionary_S(#{lang},MultiDisPlayName) AS TransMultiDisplayName ,CHAR_LENGTH(SortPath) - CHAR_LENGTH(REPLACE(SortPath,';','')) SortDepth ,GroupPath FROM covi_smart4j.sys_object_group WHERE 1=1
AND GroupCode = #{DeptCode}
OR CONCAT(';',GroupPath) LIKE CONCAT('%;',#{DeptCode},';%')
AND GroupType IN ('Dept','Company')
AND CompanyCode = #{CompanyCode}
AND IsUse = 'Y' AND IsDisplay = 'Y'
AND MemberOf != 'NOUSE' ORDER BY SortPath asc

/* attend.common.getUserListByDept */ SELECT sou.UserCode ,sou.PhotoPath ,sou.EnterDate ,covi_smart4j.Fn_BaseGetDictionary_S(#{lang},soub.MultiDeptName) DeptName ,covi_smart4j.Fn_BaseGetDictionary_S(#{lang},soub.MultiJobPositionName) JobPositionName
,IF(LENGTH(covi_smart4j.Fn_BaseGetDictionary_S(#{lang}, sou.MultiDisplayName))>0, covi_smart4j.Fn_BaseGetDictionary_S(#{lang}, sou.MultiDisplayName), sou.DisplayName) as DisplayName ,(SELECT DisplayName FROM covi_smart4j.sys_object_group r WHERE GroupCode = sog.MemberOf) DeptFullPath ,sou.IsUse ,sou.IsDisplay ,sou.EnterDate ,DeptCode FROM (select * from covi_smart4j.sys_object_user_basegroup where JobType = 'Origin' group by UserCode) soub JOIN covi_smart4j.sys_object_user sou ON soub.UserCode = sou.UserCode LEFT JOIN covi_smart4j.sys_object_group sog ON soub.DeptCode = sog.GroupCode WHERE soub.CompanyCode = #{CompanyCode}
AND sog.GroupPath LIKE CONCAT(#{groupPath},'%')
#{dept.GroupCode}
AND sou.IsUse = 'Y' AND sou.IsDisplay = 'Y'
AND sou.IsUse = 'N' AND sou.IsDisplay = 'N'
AND soub.JobTitleCode = #{sJobTitleCode}
AND soub.JobLevelCode = #{sJobLevelCode}
AND soub.DeptCode = #{sDeptCode}
AND sou.UserCode = #{sUserCode}
AND sou.DisplayName LIKE CONCAT('%',#{sUserTxt},'%')
AND sou.MultiDisplayName LIKE concat('%', #{KeyWord} ,'%')
JobTitleSortKey ASC
JobLevelSortKey ASC
JobPositionSortKey ASC
DisplayName ASC
, soub.SortKey, soub.JobTitlecode, sou.EnterDate, DisplayName ASC
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT
	COUNT(*)
FROM (select * from covi_smart4j.sys_object_user_basegroup where JobType = 'Origin' group by UserCode) soub
JOIN covi_smart4j.sys_object_user sou
ON soub.UserCode = sou.UserCode
LEFT JOIN covi_smart4j.sys_object_group sog
ON soub.DeptCode = sog.GroupCode
WHERE soub.CompanyCode = #{CompanyCode}

	AND  sog.GroupPath LIKE CONCAT(#{groupPath},'%')



		#{dept.GroupCode}





			AND sou.IsUse = 'Y'
			AND sou.IsDisplay = 'Y'


			AND sou.IsUse = 'N'
			AND sou.IsDisplay = 'N'





	AND soub.JobTitleCode = #{sJobTitleCode}



	AND soub.JobLevelCode = #{sJobLevelCode}



	AND soub.DeptCode = #{sDeptCode}


	AND sou.UserCode = #{sUserCode}


	AND sou.DisplayName LIKE CONCAT('%',#{sUserTxt},'%')


	AND sou.MultiDisplayName LIKE concat('%', #{KeyWord} ,'%')

/* attend.common.getMyUserInfo */ SELECT sou.UserCode ,sou.PhotoPath ,sou.EnterDate ,covi_smart4j.Fn_BaseGetDictionary_S(#{lang},soub.MultiDeptName) DeptName ,covi_smart4j.Fn_BaseGetDictionary_S(#{lang},soub.MultiJobPositionName) JobPositionName ,covi_smart4j.Fn_BaseGetDictionary_S(#{lang},sou.MultiDisplayName) DisplayName ,(SELECT DisplayName FROM covi_smart4j.sys_object_group r WHERE GroupCode = sog.MemberOf) DeptFullPath ,sou.IsUse ,sou.IsDisplay ,DeptCode FROM ( SELECT * FROM covi_smart4j.sys_object_user_basegroup WHERE DeptCode = #{DeptCode} ) soub JOIN ( SELECT * FROM covi_smart4j.sys_object_user WHERE UserCode = #{UserCode} ) sou ON soub.UserCode = sou.UserCode LEFT JOIN covi_smart4j.sys_object_group sog ON soub.DeptCode = sog.GroupCode WHERE soub.CompanyCode = #{CompanyCode}
AND soub.DeptCode = #{sDeptCode}
AND sou.UserCode = #{sUserCode}

SELECT
	COUNT(*)
FROM
(
SELECT * FROM covi_smart4j.sys_object_user_basegroup
WHERE DeptCode = #{DeptCode}
) soub
JOIN
(
SELECT * FROM covi_smart4j.sys_object_user
WHERE UserCode = #{UserCode}
) sou
ON soub.UserCode = sou.UserCode
LEFT JOIN covi_smart4j.sys_object_group sog
ON soub.DeptCode = sog.GroupCode
WHERE soub.CompanyCode = #{CompanyCode}

	AND soub.DeptCode = #{sDeptCode}


	AND sou.UserCode = #{sUserCode}

SELECT
AttSeq
FROM covi_smart4j.attendance_mng_mst
WHERE ValidYn = #{ValidYn}
AND CompanyCode = #{CompanyCode}

SELECT amjs.JobStsSeq ,amjs.JobStsName ,amjs.ValidYn ,amjs.Memo ,amjs.ReqMethod ,amjs.UpdMethod ,amjs.DelMethod FROM covi_smart4j.attendance_mng_job_status amjs JOIN covi_smart4j.attendance_mng_mst amm ON amm.AttSeq = amjs.AttSeq AND amm.CompanyCode = #{CompanyCode} WHERE 1=1
AND JobStsSeq = #{JobStsSeq}
AND amjs.ValidYn = #{ValidYn}
ORDER BY amjs.JobStsSeq DESC

/* queryID : attend.common.getHolidaySchedule */
SELECT		dayList, HolidayStart, HolidayEnd, HolidayName
FROM		covi_smart4j.attendance_mng_holiday_schedule
LEFT   JOIN covi_smart4j.attendance_daylist ad ON ad.dayList BETWEEN HolidayStart AND HolidayEnd
WHERE		CompanyCode = #{CompanyCode}

AND	        #{StartDate} BETWEEN DATE_FORMAT(HolidayStart,'%Y%m') AND DATE_FORMAT(HolidayEnd,'%Y%m')

	SELECT ConfigID, BizSection, SettingKey ,SettingValue
	  FROM covi_smart4j.sys_base_config a
INNER JOIN covi_smart4j.sys_object_domain B ON A.DomainID = B.DomainID
	 WHERE b.DomainCode =  #{CompanyCode}
	   AND a.SettingKey =  #{settingVal}
