mapper.select:
SELECT A.UserCode , A.DisplayName , C.DeptName , A.VacDay , A.UseDays , A.ATot FROM ( SELECT IFNULL(SUM(A.VACDAY), 0) AS VacDay , FORMAT(SUM(A.DAYSREQ) + SUM(A.DAYSCAN), 1) AS UseDays , IFNULL(SUM(A.VACDAY) - (SUM(A.DAYSREQ) + SUM(A.DAYSCAN)) - SUM(A.DAYSNEXT), 0) AS ATot , A.UserCode , covi_smart4j.Fn_BaseGetDictionary_S(#{lang}, B.MultiDisplayName) AS DisplayName FROM (
SELECT FORMAT(IFNULL(VacDay, 0), 1) AS VACDAY , 0 AS DAYSREQ , 0 AS DAYSCAN , 0 AS DAYSNEXT , #{UserCode} AS UserCode FROM covi_smart4j.VM_VACATIONPLAN WHERE YEAR = #{VacYear} AND UR_Code = #{UserCode}
UNION ALL
SELECT 0 AS VACDAY , IFNULL(SUM(CASE WHEN GUBUN IN ('VACATION_APPLY', 'VACATION_PUBLIC') AND (Reserved2 IS NULL OR Reserved2 = '') THEN VacDay END), 0) AS DAYSREQ , IFNULL(SUM(CASE WHEN GUBUN IN ('VACATION_CANCEL', 'VACATION_PUBLIC_CANCEL') AND (Reserved2 IS NULL OR Reserved2 = '') THEN VacDay END), 0) AS DAYSCAN , IFNULL(SUM(CASE WHEN GUBUN IN ('VACATION_APPLY', 'VACATION_PUBLIC') AND Reserved2 = '1' THEN VacDay END), 0) AS DAYSNEXT , #{UserCode} AS UserCode FROM covi_smart4j.VM_VACATIONINFO vi JOIN covi_smart4j.SYS_BASE_CODE bc ON ( bc.DomainID = (SELECT DomainID FROM covi_smart4j.sys_object_domain WHERE DomainCode = #{CompanyCode}) AND CodeGroup = 'VACATION_TYPE' AND IsUse = 'Y' AND Reserved1 = '+' AND VacFlag = bc.Code ) WHERE vi.VacYear = #{VacYear} AND vi.UR_Code = #{UserCode}
) A JOIN covi_smart4j.sys_object_user B ON A.UserCode = B.UserCode GROUP BY A.UserCode, B.MultiDisplayName ) A JOIN covi_smart4j.sys_object_user_basegroup C ON C.UserCode = A.UserCode AND C.JobType = 'Origin' WHERE A.ATot <= 0
DisplayName ASC
DeptName Atot DisplayName
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT COUNT(0) FROM ( SELECT IFNULL(SUM(VACDAY), 0) AS VacDay , FORMAT(SUM(DAYSREQ) + SUM(DAYSCAN), 1) AS UseDays , IFNULL(SUM(VACDAY) - (SUM(DAYSREQ) + SUM(DAYSCAN)) - SUM(DAYSNEXT), 0) AS ATot , UserCode FROM (
SELECT FORMAT(IFNULL(VacDay, 0), 1) AS VACDAY , 0 AS DAYSREQ , 0 AS DAYSCAN , 0 AS DAYSNEXT , #{UserCode} AS UserCode FROM covi_smart4j.VM_VACATIONPLAN WHERE YEAR = #{VacYear} AND UR_Code = #{UserCode}
UNION ALL
SELECT 0 AS VACDAY , IFNULL(SUM(CASE WHEN GUBUN IN ('VACATION_APPLY', 'VACATION_PUBLIC') AND (Reserved2 IS NULL OR Reserved2 = '') THEN VacDay END), 0) AS DAYSREQ , IFNULL(SUM(CASE WHEN GUBUN IN ('VACATION_CANCEL', 'VACATION_PUBLIC_CANCEL') AND (Reserved2 IS NULL OR Reserved2 = '') THEN VacDay END), 0) AS DAYSCAN , IFNULL(SUM(CASE WHEN GUBUN IN ('VACATION_APPLY', 'VACATION_PUBLIC') AND Reserved2 = '1' THEN VacDay END), 0) AS DAYSNEXT , #{UserCode} AS UserCode FROM covi_smart4j.VM_VACATIONINFO vi JOIN covi_smart4j.SYS_BASE_CODE bc ON ( bc.DomainID = (SELECT DomainID FROM covi_smart4j.sys_object_domain WHERE DomainCode = #{CompanyCode}) AND CodeGroup = 'VACATION_TYPE' AND IsUse = 'Y' AND Reserved1 = '+' AND VacFlag = bc.Code ) WHERE vi.VacYear = #{VacYear} AND vi.UR_Code = #{UserCode}
) A GROUP BY UserCode ) A WHERE A.ATot <= 0

mapper.update:
	/* attend.req.updateApprovalCommuteStatus */
	UPDATE covi_smart4j.attendance_commuting_mst
	SET
	StartSts = #{Status}
	EndSts = #{Status}
    WHERE UserCode = #{UserCode} AND TargetDate = #{TargetDate}

mapper.select:
/* attend.req.selectRequestList */
SELECT ReqType, ReqData
FROM covi_smart4j.attendance_request
WHERE ReqSeq = #{ReqSeq}
