mapper.select:
/* attend.schedule.getAttendScheduleList*/ SELECT * FROM( SELECT ams.AttSeq ,ams.SchName ,ams.SchSeq ,ams.WorkZone ,ams.AttDayStartTime ,ams.AttDayEndTime , concat(ams.StartSunHour,':',StartSunMin,'~',EndSunHour,':',EndSunMin) WorkTimeSun , concat(ams.StartMonHour,':',StartMonMin,'~',EndMonHour,':',EndMonMin) WorkTimeMon , concat(ams.StartTueHour,':',StartTueMin,'~',EndTueHour,':',EndTueMin) WorkTimeTue , concat(ams.StartWedHour,':',StartWedMin,'~',EndWedHour,':',EndWedMin) WorkTimeWed , concat(ams.StartThuHour,':',StartThuMin,'~',EndThuHour,':',EndThuMin) WorkTimeThe , concat(ams.StartFriHour,':',StartFriMin,'~',EndFriHour,':',EndFriMin) WorkTimeFri , concat(ams.StartSatHour,':',StartSatMin,'~',EndSatHour,':',EndSatMin) WorkTimeSat ,ams.NextDayYnSun ,ams.NextDayYnMon ,ams.NextDayYnTue ,ams.NextDayYnWed ,ams.NextDayYnThu ,ams.NextDayYnFri ,ams.NextDayYnSat ,ams.AssYn ,ams.Memo ,ams.BaseYn ,(SELECT COUNT(*) FROM attendance_mng_schedule_member amm WHERE amm.SchSeq = ams.SchSeq) MemberCnt ,(SELECT GROUP_CONCAT(ur.DisplayName SEPARATOR ',') FROM attendance_mng_schedule_member mem, sys_object_user ur WHERE mem.SchSeq = ams.SchSeq AND mem.UserCode = ur.UserCode) MemberList ,(SELECT COUNT(*) FROM attendance_mng_schedule_alloc ama WHERE ama.SchSeq = ams.SchSeq) AllocCnt ,CONCAT(ams.WorkZone,',', wpinfo.wplist ) as WorkPlace FROM covi_smart4j.attendance_mng_schedule ams LEFT JOIN (SELECT swp.SchSeq, GROUP_CONCAT(wp.WorkZone SEPARATOR ',') as wplist FROM attendance_mng_schedule_workplace swp, attendance_workplace wp WHERE swp.LocationSeq = wp.LocationSeq GROUP BY swp.SchSeq ) wpinfo ON wpinfo.SchSeq = ams.SchSeq WHERE ams.CompanyCode = #{CompanyCode} AND ams.ValidYn = 'Y'
AND ams.SchSeq = #{SchSeq}
) tbl
WHERE
SchName LIKE CONCAT('%', #{schTxt}, '%') OR WorkPlace LIKE CONCAT('%', #{schTxt}, '%') OR MemberList LIKE CONCAT('%', #{schTxt}, '%') OR Memo LIKE CONCAT('%', #{schTxt}, '%')
SchName LIKE CONCAT('%', #{schTxt}, '%')
WorkPlace LIKE CONCAT('%', #{schTxt}, '%')
MemberList LIKE CONCAT('%', #{schTxt}, '%')
Memo LIKE CONCAT('%', #{schTxt}, '%')
,AttSeq ,SchSeq
, BaseYn SchName WorkZone WorkTimeMon AssYn MemberCnt AllocCnt Memo AttSeq, SchSeq
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

