mapper.select:
SELECT IFNULL(COUNT(1),0) CNT FROM ( SELECT amm.AttSeq ,amm.CompanyCode ,ams.SchSeq ,amsm.ScMemSeq ,ams.SchName ,ams.MultiDisplayName ,Fn_BaseGetDictionary_S(#{lang},ams.MultiDisplayName) schMulti ,sou.DisplayName AS UserName ,Fn_BaseGetDictionary_S(#{lang},sou.MultiDisplayName) userMulti ,Fn_BaseGetDictionary_S(#{lang},sou.MultiDeptName) TransChargeBusiness ,amsm.UserCode ,amsm.StartDtm ,amsm.EndDtm ,amsm.ValidYn ,#{UR_TimeZone} AS NowDate FROM covi_smart4j.attendance_mng_schedule_member amsm LEFT JOIN ( SELECT ur.*,bg.MultiDeptName,bg.DeptCode,bg.JobType FROM covi_smart4j.sys_object_user_basegroup bg LEFT JOIN covi_smart4j.sys_object_user AS ur ON ur.UserCode = bg.UserCode ) sou ON sou.UserCode = amsm.UserCode AND sou.JobType = 'Origin' LEFT JOIN covi_smart4j.attendance_mng_schedule ams ON ams.SchSeq = amsm.SchSeq LEFT JOIN covi_smart4j.attendance_mng_mst amm ON ams.AttSeq = amm.AttSeq WHERE 1=1 AND sou.IsUse = 'Y' AND ams.ValidYn = 'Y' AND amm.ValidYn = 'Y' AND amm.CompanyCode = #{CompanyCode}
/*근무제 사용여부*/ AND amm.AttYn = #{AttYn}
/*근무제 활성여부*/ AND amsm.ValidYn = #{ValidCheck}
/*특정고객조회시*/ AND amsm.UserCode = #{p_UserCode}
/*근무제 조회*/ AND amsm.SchSeq = #{SchSeq}
/*근무제 지정자 번호 조회*/ AND amsm.ScMemSeq = #{ScMemSeq}
/*날자검색 추가*/ AND amsm.StartDtm BETWEEN DATE_FORMAT(#{S_StartDate},'%Y.%m.%d') AND DATE_FORMAT(#{S_EndDate},'%Y.%m.%d')
/*지정자명 검색 추가*/ AND sou.DisplayName LIKE CONCAT('%', #{S_Specifier}, '%')
) m WHERE 1=1
/*당일기준 */ AND m.NowDate BETWEEN m.StartDtm AND m.EndDtm
/*저장일 기준 */ AND ( m.StartDtm BETWEEN DATE_FORMAT(#{P_StartDtm},'%Y.%m.%d') AND DATE_FORMAT(#{P_EndDtm},'%Y.%m.%d') OR m.EndDtm BETWEEN DATE_FORMAT(#{P_StartDtm},'%Y.%m.%d') AND DATE_FORMAT(#{P_EndDtm},'%Y.%m.%d') OR DATE_FORMAT(#{P_StartDtm},'%Y.%m.%d') BETWEEN m.StartDtm AND m.EndDtm OR DATE_FORMAT(#{P_EndDtm},'%Y.%m.%d') BETWEEN m.StartDtm AND m.EndDtm )
/*지정일기준 */ AND DATE_FORMAT(#{S_TargetDate},'%Y.%m.%d') BETWEEN m.StartDtm AND m.EndDtm

SELECT m.AttSeq ,m.CompanyCode ,m.SchSeq ,m.ScMemSeq ,m.SchName ,m.MultiDisplayName ,m.schMulti ,m.UserName ,m.userMulti ,m.TransChargeBusiness ,m.UserCode ,m.StartDtm ,m.EndDtm ,m.ValidYn ,m.NowDate ,m.PcUseYn ,m.MobileUseYn ,CASE WHEN m.NowDate < m.StartDtm THEN 'A1' WHEN m.NowDate <= m.EndDtm THEN 'A2' WHEN m.NowDate > m.EndDtm THEN 'A3' END Sts ,CASE WHEN m.NowDate >= m.EndDtm THEN 'N' ELSE ValidYn END Valid FROM ( SELECT amm.AttSeq ,amm.CompanyCode ,ams.SchSeq ,amsm.ScMemSeq ,ams.SchName ,ams.MultiDisplayName ,Fn_BaseGetDictionary_S(#{lang},ams.MultiDisplayName) schMulti ,sou.DisplayName AS UserName ,Fn_BaseGetDictionary_S(#{lang},sou.MultiDisplayName) userMulti ,Fn_BaseGetDictionary_S(#{lang},sou.MultiDeptName) TransChargeBusiness ,amsm.UserCode ,amsm.StartDtm ,amsm.EndDtm ,amsm.ValidYn ,#{UR_TimeZone} AS NowDate ,amm.PcUseYn ,amm.MobileUseYn FROM covi_smart4j.attendance_mng_schedule_member amsm LEFT JOIN ( SELECT ur.*,bg.MultiDeptName,bg.DeptCode,bg.JobType FROM covi_smart4j.sys_object_user_basegroup bg LEFT JOIN covi_smart4j.sys_object_user AS ur ON ur.UserCode = bg.UserCode ) sou ON sou.UserCode = amsm.UserCode AND sou.JobType = 'Origin' LEFT JOIN covi_smart4j.attendance_mng_schedule ams ON ams.SchSeq = amsm.SchSeq LEFT JOIN covi_smart4j.attendance_mng_mst amm ON ams.AttSeq = amm.AttSeq WHERE 1=1 AND sou.IsUse = 'Y' AND ams.ValidYn = 'Y' AND amm.ValidYn = 'Y' AND amm.CompanyCode = #{CompanyCode}
/*근무제 사용여부*/ AND amm.AttYn = #{AttYn}
/*근무제 활성여부*/ AND amsm.ValidYn = #{ValidCheck}
/*특정고객조회시*/ AND amsm.UserCode = #{p_UserCode}
/*근무제 조회*/ AND amsm.SchSeq = #{SchSeq}
/*근무제 지정자 번호 조회*/ AND amsm.ScMemSeq = #{ScMemSeq}
/*날자검색 추가*/ AND amsm.StartDtm BETWEEN DATE_FORMAT(#{S_StartDate},'%Y.%m.%d') AND DATE_FORMAT(#{S_EndDate},'%Y.%m.%d')
/*지정자명 검색 추가*/ AND sou.DisplayName LIKE CONCAT('%', #{S_Specifier}, '%')
) m WHERE 1=1
/*당일기준 */ AND m.NowDate BETWEEN m.StartDtm AND m.EndDtm
/*저장일 기준 */ AND ( m.StartDtm BETWEEN DATE_FORMAT(#{P_StartDtm},'%Y.%m.%d') AND DATE_FORMAT(#{P_EndDtm},'%Y.%m.%d') OR m.EndDtm BETWEEN DATE_FORMAT(#{P_StartDtm},'%Y.%m.%d') AND DATE_FORMAT(#{P_EndDtm},'%Y.%m.%d') OR DATE_FORMAT(#{P_StartDtm},'%Y.%m.%d') BETWEEN m.StartDtm AND m.EndDtm OR DATE_FORMAT(#{P_EndDtm},'%Y.%m.%d') BETWEEN m.StartDtm AND m.EndDtm )
/*지정일기준 */ AND DATE_FORMAT(#{S_TargetDate},'%Y.%m.%d') BETWEEN m.StartDtm AND m.EndDtm
ORDER BY Valid ,StartDtm
LIMIT #{pageSize} OFFSET #{pageOffset}

mapper.insert:
INSERT INTO covi_smart4j.attendance_mng_schedule_member
(
	ScMemSeq
	,SchSeq
	,UserCode
	,StartDtm
	,EndDtm
	,ValidYn
	,CompanyCode
	,RegisterCode
	,RegistDate
	,ModifyerCode
	,ModifyDate
)
VALUES
(
	 #{ScMemSeq}
	,#{SchSeq}
	,#{p_UserCode}
	,DATE_FORMAT(#{StartDtm},'%Y.%m.%d')
	,DATE_FORMAT(#{EndDtm},'%Y.%m.%d')
	,#{ValidYn}
	,#{CompanyCode}
	,#{UserCode}
	,now()
	,#{UserCode}
	,now()
)ON DUPLICATE KEY UPDATE    
    ModifyerCode   = #{UserCode}    
    ,ModifyDate     = NOW()      

    ,StartDtm        = DATE_FORMAT(#{StartDtm},'%Y.%m.%d')


    ,EndDtm          = DATE_FORMAT(#{EndDtm},'%Y.%m.%d')    


	,ValidYn        = #{ValidYn}

mapper.select:
SELECT LocationSeq , WorkZoneGroupNm , WorkZone , ValidYn FROM covi_smart4j.attendance_workplace WHERE CompanyCode IN ('ORGROOT', #{CompanyCode})
AND LocationSeq NOT IN ( SELECT LocationSeq FROM covi_smart4j.attendance_mng_schedule_workplace WHERE SchSeq = #{SchSeq} AND WorkPlaceType = #{WorkPlaceType} )
AND ValidYn = #{validYn}

