mapper.select:
/* collab.common.getUserProject */ SELECT PrjSeq, PrjName, PrjStatus, ProgRate, IsClose, DATE_FORMAT(CloseDate ,'%Y-%m-%d %H:%i') CloseDate , PrjColor, Remark, StartDate, EndDate, RegisterCode, DATE_FORMAT(RegisteDate ,'%Y-%m-%d %H:%i') RegisteDate ,(SELECT GROUP_CONCAT(CONCAT(ctm.UserCode,'^',covi_smart4j.Fn_BaseGetDictionary_S('',sou.MultiDisplayName),'^',sou.PhotoPath) SEPARATOR '|') FROM covi_smart4j.collab_project_manager ctm JOIN covi_smart4j.sys_object_user sou on ctm.userCode = sou.userCode WHERE ctm.PrjSeq = p.PrjSeq) MmUser ,(SELECT covi_smart4j.Fn_BaseGetDictionary_S(#{lang},sou.MultiDisplayName) FROM covi_smart4j.collab_project_member ctm JOIN covi_smart4j.sys_object_user sou on ctm.userCode = sou.userCode WHERE ctm.PrjSeq = p.PrjSeq LIMIT 1) TmUser ,(SELECT count(ctm.userCode) FROM covi_smart4j.collab_project_member ctm JOIN covi_smart4j.sys_object_user sou on ctm.userCode = sou.userCode WHERE ctm.PrjSeq = p.PrjSeq) TmUserCnt ,IsMember ,IsManager ,IsRegister ,IsViewer ,IsFav FROM ( SELECT distinct cp.PrjSeq, cp.PrjName, cp.PrjStatus, cp.ProgRate, cp.IsClose, cp.CloseDate, cp.PrjColor, cp.Remark, cp.StartDate, cp.EndDate, cp.RegisterCode, cp.RegisteDate , case when cpm.PrjSeq IS NOT NULL then 'Y' ELSE 'N' end IsMember , case when cpn.PrjSeq IS NOT NULL then 'Y' ELSE 'N' end IsManager , case when cpr.PrjSeq IS NOT NULL then 'Y' ELSE 'N' end IsRegister , case when cpv.PrjSeq IS NOT NULL then 'Y' ELSE 'N' end IsViewer , IFNULL((SELECT COUNT(*) FROM covi_smart4j.collab_project_favorite WHERE cp.PrjSeq = PrjSeq AND UserCode = #{USERID}),0) IsFav FROM collab_project cp LEFT JOIN collab_project_member cpm ON cp.PrjSeq = cpm.PrjSeq AND cpm.UserCode = #{USERID} LEFT JOIN collab_project_manager cpn ON cp.PrjSeq = cpn.PrjSeq AND cpn.UserCode = #{USERID} LEFT JOIN collab_project cpr ON cp.PrjSeq = cpr.PrjSeq AND cpr.RegisterCode = #{USERID} LEFT JOIN collab_project_viewer cpv ON cp.PrjSeq = cpv.PrjSeq AND cpv.UserCode = #{USERID} WHERE 1=1
AND cp.CompanyCode = #{CompanyCode}
AND cp.IsClose='N'
AND cp.IsClose= #{inClose}
AND (cpm.PrjSeq IS NOT NULL OR cpn.PrjSeq IS NOT NULL OR cpr.PrjSeq IS NOT NULL
OR cpv.PrjSeq IS NOT NULL
)
AND (cpm.PrjSeq IS NOT NULL )
AND (cp.PrjName like concat('%', #{seValue}, '%') or cp.Remark like concat('%', #{seValue}, '%'))
AND cp.PrjStatus in
#{item}
) p
ORDER BY RegisteDate desc LIMIT #{pageSize} OFFSET #{pageOffset}
ORDER BY PrjName asc

/* collab.common.getUserProjectCnt */
	SELECT COUNT(PrjSeq)
	FROM   (
		SELECT distinct cp.PrjSeq, cp.PrjName, cp.PrjStatus, cp.ProgRate, cp.CloseDate, cp.PrjColor, cp.Remark, cp.StartDate, cp.EndDate, cp.RegisterCode, cp.RegisteDate
		  FROM collab_project cp
	LEFT  JOIN collab_project_member cpm ON cp.PrjSeq = cpm.PrjSeq AND cpm.UserCode = #{USERID}
	LEFT  JOIN collab_project_manager cpn ON cp.PrjSeq = cpn.PrjSeq AND cpn.UserCode = #{USERID}
	LEFT  JOIN collab_project cpr ON cp.PrjSeq = cpr.PrjSeq AND cpr.RegisterCode = #{USERID}
	LEFT  JOIN collab_project_viewer cpv ON cp.PrjSeq = cpv.PrjSeq AND cpv.UserCode = #{USERID}
		 WHERE 1=1 

		 	 AND cp.CompanyCode = #{CompanyCode}


		   AND cp.IsClose='N'


		   AND cp.IsClose= #{inClose}


		   AND (cpm.PrjSeq IS NOT NULL OR  cpn.PrjSeq IS NOT NULL OR   cpr.PrjSeq IS NOT NULL 

		   	OR   cpv.PrjSeq IS NOT NULL 

		   )


		   AND (cp.PrjName like concat('%', #{seValue}, '%')
		   	or cp.Remark like concat('%', #{seValue}, '%'))


			AND cp.PrjStatus  in 

                    #{item}


			) p   

		LIMIT #{pageSize} OFFSET #{pageOffset}

SELECT DashThema, TaskShowCode
  FROM collab_user_conf
 WHERE UserCode = #{USERID}
