mapper.select:
/* collab.hist.getTaskHistListCnt */
	SELECT count(h.histseq) 	     	
	FROM   collab_task_history h
	LEFT JOIN collab_task t ON h.TaskSeq = t.taskseq 
	LEFT JOIN sys_object_user D on D.UserCode = h.registercode

		 JOIN collab_task_map C on h.taskseq = c.taskseq
		 JOIN collab_project P on c.PrjType='P' and p.PrjSeq = c.PrjSeq
			AND P.PrjSeq in 

                    #{item}


   WHERE	h.RegisteDate between  STR_TO_DATE(CONCAT(#{startDate},'000000') ,'%Y%m%d%H%i%s')   and STR_TO_DATE(CONCAT(#{endDate},'235959') ,'%Y%m%d%H%i%s')  	 

			AND h.registercode in 

                    #{item}



	  	 	AND modItem  = #{modItem}

/* collab.hist.getTaskHistListCnt */ SELECT DATE_FORMAT(h.RegisteDate ,'%Y-%m-%d %H:%i') RegisteDate, h.RegisterCode, t.TaskName , h.ModType , h.ModItem, h.BfVal, h.AfVal ,Fn_BaseGetDictionary_S(#{lang}, d.MultiDisplayName) RegisterName ,ModType, Fn_GetBaseCodeName(0,'HistoryType',modType) ModTypeName ,ModItem, Fn_GetBaseCodeName(0,'CollabHistory',modItem) ModItemName ,(SELECT concat(c.PrjType ,'^', case when c.prjType = 'P' then p.PrjName ELSE t.DisplayName END ,'^', ifnull(PrjColor,''),'^', s.SectionName) FROM collab_task_map C JOIN collab_section s on c.sectionseq = s.sectionseq LEFT JOIN collab_project P on c.PrjType='P' and p.PrjSeq = c.PrjSeq LEFT JOIN sys_object_group T on c.PrjType!='P' and t.GroupId = c.PrjSeq WHERE C.TaskSeq = h.TaskSeq limit 1) PrjDesc , (SELECT count(*) FROM collab_task_map WHERE TaskSeq = h.TaskSeq) as PrjCount , case modItem WHEN 'Section' then (SELECT SectionName FROM collab_section s WHERE s.SectionSeq = afval) WHEN 'Status' then Fn_GetBaseCodeName(0,'CollabStatus', AfVal) ELSE AfVal END AfValName , case modItem WHEN 'Section' then (SELECT SectionName FROM collab_section s WHERE s.SectionSeq = bfval) WHEN 'Status' then Fn_GetBaseCodeName(0,'CollabStatus', BfVal) ELSE BfVal END BfValName FROM collab_task_history h LEFT JOIN collab_task t ON h.TaskSeq = t.taskseq LEFT JOIN sys_object_user D on D.UserCode = h.registercode
JOIN collab_task_map C on h.taskseq = c.taskseq JOIN collab_project P on c.PrjType='P' and p.PrjSeq = c.PrjSeq AND P.PrjSeq in
#{item}
WHERE h.RegisteDate between STR_TO_DATE(CONCAT(#{startDate},'000000') ,'%Y%m%d%H%i%s') and STR_TO_DATE(CONCAT(#{endDate},'235959') ,'%Y%m%d%H%i%s')
AND h.registercode in
#{item}
AND modItem = #{modItem}
h.RegisteDate RegisterName t.TaskName ModItemName ModTypeName RegisteDate
ASC DESC
h.RegisteDate desc
LIMIT #{pageSize} OFFSET #{pageOffset}
