mapper.select:
/* collab.report.getUserTaskDayList */
SELECT a.TaskSeq , a.PrjDesc , a.PrjCount , a.TaskName , a.TaskStatus , a.ProgRate , a.Remark as 'Remark' FROM ( SELECT ct.TaskSeq , ct.TaskName , ct.TaskStatus , ct.ProgRate , ct.Remark , (SELECT concat(m.prjType,'^',case when m.prjType='P' then cp.PrjName else Fn_BaseGetDictionary_S(#{lang}, sg.MultiDisplayName) end ,'^', TaskSeq ,'^', SectionName) FROM covi_smart4j.collab_task_map m JOIN covi_smart4j.collab_section s on m.sectionSeq = s.sectionseq LEFT JOIN covi_smart4j.collab_project cp on m.PrjType = 'P' and cp.PrjSeq = m.PrjSeq LEFT JOIN covi_smart4j.sys_object_group sg on m.PrjType != 'P' and sg.GroupID = m.PrjSeq WHERE TaskSeq = ct.TaskSeq order by cp.PrjSeq DESC LIMIT 1 ) as PrjDesc , (SELECT count(*) FROM collab_task_map WHERE TaskSeq = ct.TaskSeq) as PrjCount FROM covi_smart4j.collab_task ct INNER JOIN covi_smart4j.collab_task_member ctmember on ctmember.UserCode = #{USERID} and ctmember.TaskSeq = ct.TaskSeq LEFT JOIN covi_smart4j.collab_report_day rd on rd.ReporterCode =ctmember.UserCode and rd.ReportDate = #{reportDate} and ct.TaskSeq = rd.taskSeq WHERE rd.taskSeq is null AND ct.ParentKey = 0 AND (ct.TaskStatus != 'C' OR (#{reportDate} <= ct.CloseDate and ct.TaskStatus = 'C')) ) a WHERE PrjCount> 0 ORDER BY PrjDesc, a.TaskSeq
LIMIT #{pageSize} OFFSET #{pageOffset}

    /* collab.report.getUserCollabReportDayListCnt */
		  SELECT IFNULL(COUNT(1),0) CNT
         	FROM covi_smart4j.collab_report_day crd
	  INNER JOIN collab_task ct on ct.TaskSeq = crd.TaskSeq
		   WHERE crd.ReporterCode = #{USERID}
             AND crd.ReportDate = #{reportDate}

mapper.update:
/* collab.report.updateUserCollabReportDay */
    update covi_smart4j.collab_report_day
    set TaskStatus = #{taskStatus}
        , ProgRate = #{progRate}
        , TaskTime = #{taskTime}
        , Remark = #{remark}
        , ModifierCode = #{USERID}
        , ModifyDate = now()
    where      DaySeq = #{daySeq}

mapper.delete:
/* collab.report.deleteUserCollabReportDay */
    delete
    from covi_smart4j.collab_report_day
    where
        DaySeq in

            #{daySeq}

mapper.select:
/* collab.report.getUserCollabReportWeekList */ SELECT crd.DaySeq as "DaySeq" , crd.ReportDate as "ReportDate" , crd.TaskSeq as "TaskSeq" , ctmap.PrjType as "PrjType" , ctmap.PrjSeq as "PrjSeq" , ct.TaskName as "TaskName" , crd.TaskStatus as "TaskStatus" , crd.ProgRate as "ProgRate" , crd.TaskTime as "TaskTime" , crd.Remark as "Remark" , s.SectionName as "SectionName" , (select count(*) from COLLAB_TASK_MAP where TaskSeq = crd.TaskSeq) as PrjCount FROM covi_smart4j.collab_report_day crd INNER JOIN covi_smart4j.collab_task ct on ct.TaskSeq = crd.TaskSeq INNER JOIN covi_smart4j.collab_task_map ctmap on ctmap.TaskSeq = ct.TaskSeq AND ctmap.PrjSeq = #{prjSeq}
AND ctmap.PrjType = 'P'
AND ctmap.PrjType != 'P'
LEFT JOIN collab_section s on ctmap.sectionSeq = s.sectionseq WHERE crd.ReporterCode = #{USERID} AND crd.ReportDate between #{startDate} and #{endDate} order by ReportDate, DaySeq
LIMIT #{pageSize} OFFSET #{pageOffset}

    /* collab.report.getUserCollabReportWeekListCnt */
         select IFNULL(COUNT(1),0) CNT
           FROM covi_smart4j.collab_report_day crd
     INNER JOIN covi_smart4j.collab_task ct on ct.TaskSeq = crd.TaskSeq
     INNER JOIN covi_smart4j.collab_task_map ctmap on ctmap.TaskSeq = ct.TaskSeq AND ctmap.PrjSeq = #{prjSeq}      


				AND ctmap.PrjType  = 'P'		


				AND ctmap.PrjType != 'P'		


           JOIN covi_smart4j.collab_section s on ctmap.sectionSeq = s.sectionseq
          WHERE crd.ReporterCode = #{USERID}
            AND crd.ReportDate between #{startDate} and #{endDate}

/* collab.report.getUserCollabReportWeek */
    select
        ReportSeq as 'ReportSeq'
        , WeekRemark as 'WeekRemark'
        , NextPlan as 'NextPlan'
    from covi_smart4j.collab_report
   where ReporterCode = #{USERID}
     AND PrjType  = #{prjType}
     and PrjSeq = #{prjSeq}
     and StartDate = #{startDate}
     and EndDate = #{endDate}

mapper.insert:
/* collab.report.insertUserCollabReportWeek */
    insert into covi_smart4j.collab_report(ReporterCode, PrjType, PrjSeq, StartDate, EndDate, WeekRemark, NextPlan, RegisterCode, RegistDate)
    values (
        #{USERID}
        , #{prjType}
        , #{prjSeq}
        , #{startDate}
        , #{endDate}
        , #{weekRemark}
        , #{nextPlan}
        , #{USERID}
        , now()
    )

mapper.update:
/* collab.report.updateUserCollabReportWeek */
    update covi_smart4j.collab_report
    set WeekRemark = #{weekRemark}
        , NextPlan = #{nextPlan}
        , ModifierCode = #{USERID}
        , ModifyDate = now()
    where
        ReportSeq = #{reportSeq}

mapper.delete:
/* collab.report.deleteUserCollabReportWeek */
    delete
    from covi_smart4j.collab_report
    where
        ReportSeq = #{reportSeq}

mapper.select:
    /* collab.report.getProjectReportDayList */
		SELECT crd.DaySeq, ct.TaskName, crd.TaskTime, crd.ProgRate, crd.Remark, crd.TaskStatus, crd.ReportDate, crd.TaskSeq			, s.SectionName as "SectionName"
		  FROM covi_smart4j.collab_report_day crd
	INNER JOIN covi_smart4j.collab_task ct on ct.TaskSeq = crd.TaskSeq
	INNER JOIN covi_smart4j.collab_task_map ctmap on ctmap.PrjType = #{prjType} AND  ctmap.PrjSeq =#{prjSeq} and ctmap.TaskSeq = ct.TaskSeq
	INNER JOIN covi_smart4j.COLLAB_section s on ctmap.SectionSeq = s.sectionSeq
	     WHERE crd.ReportDate BETWEEN #{startDate} and #{endDate}
	       AND crd.ReporterCode =  #{reporterCode}
	 ORDER BY  crd.ReportDate
