mapper.insert:
/* collab.task.addTask */
	INSERT INTO covi_smart4j.collab_task
				( taskName , Label, ImpLevel, ParentKey, TopParentKey, Workorder, StartDate,EndDate, CloseDate, TaskStatus, ProgRate, Remark, ObjectType, ObjectID, IsMile, RegisterCode, RegisteDate, ModifierCode, ModifyDate)
	VALUES		( #{taskName}, #{label}, #{impLevel}, #{parentKey}, #{topParentKey}, #{workOrder}, #{startDate}, #{endDate}, case when  #{taskStatus} = 'C' then date_format(NOW(),'%Y%m%d') else #{endDate} end, #{taskStatus}, #{progRate}, #{remark}, #{objectType}, #{objectID}, #{isMile}, #{USERID}, now(), #{USERID}, now())

	    SELECT LAST_INSERT_ID();

/* collab.task.addTaskBySchedule */
	INSERT INTO covi_smart4j.collab_task
				( taskName , Label, ParentKey, TopParentKey, Workorder, StartDate,EndDate, CloseDate, TaskStatus, ProgRate, Remark, ObjectType, ObjectID, RegisterCode, RegisteDate, ModifierCode, ModifyDate)
		(SELECT  #{taskName}, #{label}, #{parentKey}, #{topParentKey}, #{workOrder}, replace(StartDate,'-',''), replace(EndDate,'-',''), replace(EndDate,'-',''), #{taskStatus}, #{progRate}, #{remark}, #{objectType}, #{objectID},#{USERID}, now(), #{USERID}, now()
		   FROM covi_smart4j.event_date
		  WHERE eventid= #{objectID}
		   )

	    SELECT LAST_INSERT_ID();

/* collab.task.addTaskBySurvey */
	INSERT INTO covi_smart4j.collab_task (taskName, Label, ParentKey, TopParentKey, Workorder, StartDate, EndDate, CloseDate, TaskStatus, ProgRate, Remark, ObjectType, ObjectID, RegisterCode, RegisteDate, ModifierCode, ModifyDate)
	(
		SELECT  #{taskName}, #{label}, #{parentKey}, #{topParentKey}, #{workOrder}, #{startDate}, #{endDate}, #{endDate}, #{taskStatus}, #{progRate}, #{remark}, #{objectType}, #{objectID}, #{USERID}, now(), #{USERID}, now()
		FROM covi_smart4j.survey
		WHERE SurveyID = #{objectID}
	)

		SELECT LAST_INSERT_ID();

/* collab.task.addTaskMapBySchedule */
	INSERT INTO covi_smart4j.collab_task_map
				(prjSeq, prjType, taskSeq	,sectionSeq	,RegisterCode,RegisteDate)
	    (SELECT #{prjSeq}, #{prjType}, taskSeq,#{sectionSeq},#{USERID},now()
	       FROM covi_smart4j.collab_task
	      WHERE ObjectType = #{objectType}
	        AND ObjectID = #{objectID})
	ON DUPLICATE KEY UPDATE    
	    RegisterCode   = #{USERID}    
	    ,RegisteDate     = NOW()

/* collab.task.addTaskMapBySurvey */
	INSERT INTO covi_smart4j.collab_task_map (prjSeq, prjType, taskSeq	,sectionSeq	,RegisterCode,RegisteDate)
	(
		SELECT #{prjSeq}, #{prjType}, taskSeq, #{sectionSeq}, #{USERID}, now()
		FROM covi_smart4j.collab_task
		INNER JOIN covi_smart4j.collab_project
		WHERE ObjectType = #{objectType}
		AND ObjectID = #{objectID}
	)
	ON DUPLICATE KEY UPDATE
		  RegisterCode	= #{USERID}
		, RegisteDate	= NOW()

mapper.delete:
/* collab.task.deleteTaskMap */
	DELETE FROM covi_smart4j.collab_task_map
	WHERE taskSeq = #{taskSeq}

	 	AND prjSeq = #{prjSeq}
	 	AND prjType = #{prjType}

mapper.update:
/* collab.task.changeProjectTaskSection */
	UPDATE covi_smart4j.collab_task_map 
	   SET sectionSeq = #{sectionSeq}
	 WHERE prjSeq =  #{prjSeq}
	   AND prjType = #{prjType}
	   AND taskSeq =  #{taskSeq}

/* collab.task.changeProjectTaskOrder */

		UPDATE covi_smart4j.collab_task_map 
		   SET Workorder= #{lst.workOrder}
		 WHERE taskSeq =  #{lst.taskSeq}
		   AND prjType=  #{prjType}
		   AND prjSeq=  #{prjSeq}

/* collab.task.changeProjectTaskTodoOrder */

		UPDATE covi_smart4j.collab_task_member 
		   SET Todoorder= #{lst.todoOrder}
		 WHERE taskSeq =  #{lst.taskSeq}
		   AND userCode=  #{userCode}

/* collab.task.changeProjectTaskStatus */
	UPDATE covi_smart4j.collab_task
	   SET TaskStatus = #{taskStatus}

	   		,ProgRate= 100
	   		,closeDate = date_format(NOW() ,'%Y%m%d') 
	   		,StartDate = case when StartDate is null then date_format(NOW() ,'%Y%m%d') else StartDate end
	   		,EndDate   = case when EndDate is null  then date_format(NOW() ,'%Y%m%d') else EndDate end

	   		,ModifyDate = now()
	   		,ModifierCode = #{USERID}
	 WHERE taskSeq =  #{taskSeq}

/* collab.task.changeProjectTaskDate */
	UPDATE covi_smart4j.collab_task
	   SET StartDate = #{startDate}
	   		,EndDate   = #{endDate}

		   		,TaskStatus = #{taskStatus} 

	   		,ModifyDate = now()
	   		,ModifierCode = #{USERID}
	 WHERE taskSeq =  #{taskSeq}

mapper.insert:
