mapper.select:
/* collab.task.getTaskUserForm */
	SELECT OptionTitle, OptionType, OptionVal
	  FROM covi_smart4j.collab_task_userform 
	 WHERE taskSeq =  #{taskSeq}

/* collab.task.getTaskUserForm */
	SELECT count(*)
	  FROM covi_smart4j.collab_task_userform 
	 WHERE taskSeq =  #{taskSeq}

/* collab.task.getTaskMapCnt */
	SELECT count(*)
	  FROM covi_smart4j.collab_task_map 
	 WHERE taskSeq =  #{taskSeq}

/* collab.task.getTaskMemberCnt */
	SELECT count(*)
	  FROM covi_smart4j.collab_task_member cpm 
	  JOIN covi_smart4j.sys_object_user sou on cpm.userCode = sou.userCode
	  JOIN covi_smart4j.sys_object_user_basegroup soub		ON sou.UserCode = soub.UserCode  AND soub.JobType ='Origin'
	 WHERE cpm.taskSeq =  #{taskSeq}

mapper.insert:
/* collab.task.addTaskMemberBySchedule */
	INSERT INTO covi_smart4j.collab_task_member
				(taskSeq	,userCode	,RegisterCode,RegisteDate)
		 (SELECT t.taskSeq,a.AttenderCode,#{USERID},now()
	       FROM covi_smart4j.collab_task t 
	       join covi_smart4j.event_attendant a on t.objectid = a.eventid 
	      WHERE ObjectType = #{objectType}
	        AND ObjectID = #{objectID})
	ON DUPLICATE KEY UPDATE    
	    RegisterCode   = #{USERID}    
	    ,RegisteDate     = NOW()     ;

	INSERT INTO covi_smart4j.collab_task_member
				(taskSeq	,userCode	,RegisterCode,RegisteDate)
		 (SELECT t.taskSeq,#{USERID},#{USERID},now()
	       FROM covi_smart4j.collab_task t 
	      WHERE ObjectType = #{objectType}
	        AND ObjectID = #{objectID})
	ON DUPLICATE KEY UPDATE    
	    RegisterCode   = #{USERID}    
	    ,RegisteDate     = NOW()     ;

/* collab.task.addTaskMemberBySurvey */
	INSERT INTO covi_smart4j.collab_task_member (taskSeq, userCode, RegisterCode, RegisteDate)
	(
		SELECT t.taskSeq, a.TargetCode, #{USERID}, now()
		FROM covi_smart4j.collab_task t
		JOIN covi_smart4j.survey_target_respondent a ON t.ObjectID = a.SurveyID
		WHERE 1=1
		AND t.ObjectType = #{objectType}
		AND t.ObjectID = #{objectID}
		AND a.TargetType = 'UR'
	)
	ON DUPLICATE KEY UPDATE
		  RegisterCode	= #{USERID}
		, RegisteDate	= NOW();

	INSERT INTO covi_smart4j.collab_task_member (taskSeq, userCode, RegisterCode, RegisteDate)
	(
		SELECT t.taskSeq, a.TargetCode, #{USERID}, now()
		FROM covi_smart4j.collab_task t
		JOIN covi_smart4j.survey_target_resultview a ON t.ObjectID = a.SurveyID
		WHERE 1=1
		AND t.ObjectType = #{objectType}
		AND t.ObjectID = #{objectID}
		AND a.TargetType = 'UR'
	)
	ON DUPLICATE KEY UPDATE
		  RegisterCode	= #{USERID}
		, RegisteDate	= NOW();

mapper.delete:
/* collab.task.deleteTaskMember */
	DELETE FROM covi_smart4j.collab_task_member WHERE taskSeq =  #{taskSeq}

	 	AND userCode = #{userCode}

mapper.select:
/* collab.task.getNoAllocProject */ with recursive cte (PrjSeq, PrjName, PrjType, SectionSeq, SectionName) as (SELECT a.PrjSeq, a.PrjName,a.PrjType, a.SectionSeq, a.SectionName FROM ( SELECT a.prjSeq, a.PrjName,s.PrjType, s.SectionSeq, s.SectionName FROM collab_project a JOIN collab_section s ON a.PrjSeq = s.PrJSeq AND s.PrjType='P' LEFT JOIN collab_project_member cpm ON a.PrjSeq = cpm.PrjSeq AND cpm.UserCode = #{USERID} LEFT JOIN collab_project_manager cpn ON a.PrjSeq = cpn.PrjSeq AND cpn.UserCode = #{USERID} LEFT JOIN collab_project cpr ON A.PrjSeq = cpr.PrjSeq AND cpr.RegisterCode = #{USERID} WHERE a.PrjStatus = 'P' AND a.IsClose='N'
AND a.CompanyCode = #{CompanyCode}
AND (cpm.PrjSeq IS NOT NULL OR cpn.PrjSeq IS NOT NULL OR cpr.PrjSeq IS NOT NULL ) UNION all SELECT b.GroupID, FN_BASEGETDICTIONARY_S(#{lang}, MULTIDISPLAYNAME),s.PrjType, s.SectionSeq, s.SectionName FROM sys_object_user_basegroup a JOIN sys_object_group b ON a.DeptCode = b.GroupCode JOIN COVI_SMART4J.COLLAB_TEAM_EXEC c on b.groupid = c.groupid AND c.isClose='N' JOIN COVI_SMART4J.COLLAB_SECTION s ON b.GroupId = s.PrJSeq AND s.PrjType = concat('T',c.execyear) WHERE UserCode = #{USERID} AND B.GroupType='Dept') a LEFT OUTER JOIN collab_task_map b ON b.TaskSeq =#{taskSeq} and a.PrjType = b.PrjType AND a.PrjSeq = b.PrjSeq
WHERE b.prjSeq IS null
) SELECT PrjSeq, PrjName, PrjType, 0 SectionSeq, SectionName, 0 nodeValue, 0 pno, PrjSeq no, PrjName nodeName, 'N' rdo FROM cte GROUP BY PrjSeq, PrjType UNION ALL SELECT PrjSeq, PrjName, PrjType, SectionSeq, SectionName,SectionSeq, prjSeq pno, concat(PrjSeq,'_',SectionSeq) no, SectionName, 'Y' rdo FROM cte ORDER BY PrjType DESC, PrjSeq, SectionSeq

