mapper.select:
/* collab.tmpl.getTmplRequestList */ SELECT RequestSeq, TmplKind, a.RequesterCode, PrjSeq , Fn_BaseGetDictionary_S(#{lang}, ur.MultiDisplayName) AS URName , RequestTitle, RequestRemark , RequestStatus , DATE_FORMAT(RegisteDate ,'%Y-%m-%d %H:%i') RegisteDate , covi_smart4j.Fn_GetBaseCodeName('0', 'COLLAB_KIND', a.TmplKind) TmplKindName , covi_smart4j.Fn_GetBaseCodeName('0', 'AttendState', a.RequestStatus) StatusName FROM collab_tmpl_request a LEFT JOIN covi_smart4j.sys_object_user ur ON a.RequesterCode = ur.UserCode WHERE a.CompanyCode = #{CompanyCode}
AND RequestStatus = 'ApprovalRequest'
LIMIT #{pageSize} OFFSET #{pageOffset}

mapper.update:

/* collab.tmpl.saveTmplRequest */
	UPDATE covi_smart4j.collab_tmpl_request 
	   SET RequestStatus = #{reqStatus}
	   		, ApprovalCode = #{approvalCode}
	   		, ApprovalDate = now() 
	   		, ApprovalRemark = #{approvalRemark}
	 WHERE RequestSeq = #{requestSeq}

mapper.select:
/* collab.tmpl.getTmplList */ SELECT TmplSeq, TmplKind, TmplName, Remark , DATE_FORMAT(RegisteDate ,'%Y-%m-%d %H:%i') RegisteDate , (SELECT Fn_BaseGetDictionary_S(#{lang}, c.MultiCodeName) FROM covi_smart4j.sys_base_code c WHERE c.CodeGroup ='COLLAB_KIND' AND c.DomainID=0 AND c.Code = a.TmplKind) TmplKindName , (SELECT COUNT(*) FROM covi_smart4j.sys_like b WHERE b.TargetServiceType = 'CollabTmpl' AND a.tmplSeq = b.TargetID) LikeCnt , (SELECT COUNT(*) FROM covi_smart4j.sys_like b WHERE b.TargetServiceType = 'CollabTmpl' AND a.tmplSeq = b.TargetID AND b.RegisterCode = #{USERID}) MyLikeCnt FROM covi_smart4j.collab_tmpl_project a WHERE companyCode = #{CompanyCode}
AND TmplKind = #{tmplKind}
AND TmplName LIKE CONCAT('%',#{tmplName},'%')
order by RegisteDate DESC

mapper.delete:
/* collab.tmpl.deleteTmplProject */
		DELETE FROM covi_smart4j.collab_tmpl_project
		WHERE tmplSeq =  #{tmplSeq}

mapper.select:
/* collab.tmpl.getTmplSection */
	SELECT cs.SectionSeq, SectionName
	  FROM covi_smart4j.collab_tmpl_section cs
	 WHERE cs.TmplSeq = #{tmplSeq}
  GROUP BY cs.SectionSeq

/* collab.tmpl.getTmplTaskList */ SELECT b.TaskSeq , B.TaskName , B.Remark , B.Label , B.ParentKey , '' as StartDate , '' as EndDate , B.TaskStatus , B.CloseDate , B.ModifierCode , B.ModifyDate , B.SectionSeq , 0 Comment , 0 ProgRate ,'P' as PrjType ,#{authSave} authSave FROM collab_tmpl_task B WHERE b.TmplSeq = #{tmplSeq} AND B.ParentKey = 0
AND SectionSeq = #{sectionSeq}
ORDER BY B.workOrder ASC , B.EndDate
LIMIT #{pageSize} OFFSET #{pageOffset}

/* collab.tmpl.getTmplTaskListCnt */
	SELECT COUNT(0)
	FROM collab_tmpl_task B
   WHERE b.TmplSeq = #{tmplSeq} 
	 AND B.ParentKey = 0

		AND SectionSeq = #{sectionSeq}

mapper.update:
/* collab.tmpl.changeTmplTaskSection */
	UPDATE covi_smart4j.collab_tmpl_task 
	   SET sectionSeq = #{sectionSeq}
	 WHERE tmplSeq =  #{tmplSeq}
	   AND taskSeq =  #{taskSeq}

/* collab.tmpl.changeTmplTaskOrder */

		UPDATE covi_smart4j.collab_tmpl_task 
		   SET Workorder= #{lst.workOrder}
		 WHERE tmplSeq =  #{lst.taskSeq}

mapper.insert:
	/* collab.tmpl.saveTmpl */
		UPDATE covi_smart4j.collab_tmpl
		   SET tmplName =  #{tmplName}
   		       ,StartDate=  #{startDate}
               ,PrjColor=  #{prjColor}
               ,Remark=  #{remark}
               ,ModifierCode=  #{USERID}
               ,ModifyDate=  now()
		 WHERE prjSeq =  #{prjSeq}

mapper.delete:
/* collab.tmpl.deleteTmplTask */
	DELETE FROM covi_smart4j.collab_tmpl_task  
	 WHERE 

	 	tmplSeq =  #{tmplSeq}


	 	taskSeq =  #{taskSeq}

mapper.select:
/* collab.tmpl.getExistsTmplSection */
	SELECT COUNT(*)
	  FROM covi_smart4j.collab_tmpl_section
	 WHERE tmplSeq  = #{tmplSeq}
	   AND SectionName = #{sectionName}

mapper.insert:
/* collab.tmpl.addTmplSection */
	INSERT INTO covi_smart4j.collab_tmpl_section 
				( tmplSeq , SectionName, RegisterCode, RegisteDate, ModifierCode, ModifyDate)
	VALUES		( #{tmplSeq}, #{sectionName}, #{USERID}, now(), #{USERID}, now())

	    SELECT LAST_INSERT_ID();

/* collab.tmpl.saveTmplSection */
	UPDATE covi_smart4j.collab_tmpl_section
	   SET sectionName =#{sectionName}
	   		,ModifierCode= #{USERID}
	   		,ModifyDate = now()
	 WHERE 	sectionSeq = 	#{sectionSeq}

mapper.delete:
/* collab.tmpl.deleteTmplSection */
	DELETE FROM covi_smart4j.collab_tmpl_section 
	 WHERE 

	 	tmplSeq =  #{tmplSeq}


	 	sectionSeq =  #{sectionSeq}

mapper.insert:
/* collab.tmpl.addTmplTask */
	INSERT INTO covi_smart4j.collab_tmpl_task
				(tmplSeq,  taskName , Label, ParentKey, TopParentKey, Workorder, SectionSeq,Remark, RegisterCode, RegisteDate, ModifierCode, ModifyDate)
	VALUES		(#{tmplSeq},  #{taskName}, #{label}, #{parentKey}, #{topParentKey}, #{workOrder}, #{sectionSeq}, #{remark}, #{USERID}, now(), #{USERID}, now())

	    SELECT LAST_INSERT_ID();

	/* collab.tmpl.saveTmplTask */
		UPDATE covi_smart4j.collab_tmpl_task
		   SET taskName =  #{taskName}
               ,Remark=  #{remark}
               ,Label=  #{label}
               ,ModifierCode=  #{USERID}
               ,ModifyDate=  now()
		 WHERE taskSeq =  #{taskSeq}

mapper.select:
/* collab.tmpl.getTmplTask */
	SELECT t.*
	  FROM covi_smart4j.collab_tmpl_task t
	 WHERE taskSeq = #{taskSeq}
