mapper.update:
UPDATE covi_smart4j.sys_object_user_info
SET PortalOption = #{portalOption}
WHERE UserCode = #{userCode}

mapper.select:
SELECT IFNULL(PortalOption, '') AS PortalOption
FROM covi_smart4j.sys_object_user_info
WHERE UserCode = #{userCode}

SELECT ApprovalList.* FROM ( SELECT A.ProcessID , B.WorkItemID , C.PerformerID , A.FormInstID , A.ProcessDescriptionID , A.DocSubject AS FormSubject , D.FormPrefix , D.FormID , A.InitiatorID , covi_smart4j.Fn_BaseGetDictionary_S(#{lang}, A.InitiatorName) AS InitiatorName , C.UserCode , C.UserName , C.SubKind AS FormSubKind , DATE_FORMAT(B.Created, '%Y.%m.%d %H:%i') AS Created , B.TaskID , U.PhotoPath , E.DomainDataContext , S.SchemaContext , D.BusinessData1 , D.BusinessData2 , D.IsFile , D.IsComment , A.ProcessName , D.Priority FROM COVI_APPROVAL4J.JWF_Process AS A INNER JOIN COVI_APPROVAL4J.JWF_Workitem AS B ON A.ProcessID = B.ProcessID INNER JOIN COVI_APPROVAL4J.JWF_Performer AS C ON B.WorkItemID = C.WorkItemID INNER JOIN COVI_APPROVAL4J.JWF_ProcessDescription AS D ON A.ProcessDescriptionID = D.ProcessDescriptionID LEFT OUTER JOIN COVI_APPROVAL4J.jwf_domaindata AS E ON A.ProcessID = E.ProcessID LEFT OUTER JOIN COVI_SMART4J.sys_object_user AS U ON U.UserCode = A.InitiatorID LEFT OUTER JOIN COVI_APPROVAL4J.jwf_forms AS F ON D.FormID = F.FormID LEFT OUTER JOIN COVI_APPROVAL4J.jwf_formsschema AS S ON F.SchemaID = S.SchemaID WHERE C.UserCode = #{userID} AND C.SubKind IN ('T000','T004','T005','T006','T008','T009','T011','T016','T017','T018','T019') AND C.State = 1 AND B.State = 288 AND A.ProcessState = 288 AND A.DeleteDate IS NULL
AND (D.BusinessData1 IS NULL
OR D.BusinessData1 IN
#{item}
)
AND D.BusinessData1 = #{businessData1}
UNION ALL
SELECT A.ProcessID , B.WorkItemID , C.PerformerID , A.FormInstID , A.ProcessDescriptionID , A.DocSubject AS FormSubject , D.FormPrefix , D.FormID , A.InitiatorID , covi_smart4j.Fn_BaseGetDictionary_S(#{lang}, A.InitiatorName) AS InitiatorName , C.UserCode , C.UserName , C.SubKind AS FormSubKind , DATE_FORMAT(B.Created, '%Y.%m.%d %H:%i') AS Created , B.TaskID , U.PhotoPath , E.DomainDataContext , S.SchemaContext , D.BusinessData1 , D.BusinessData2 , D.IsFile , D.IsComment , A.ProcessName , D.Priority FROM COVI_APPROVAL4J.JWF_Process AS A INNER JOIN COVI_APPROVAL4J.JWF_Workitem AS B ON A.ProcessID = B.ProcessID INNER JOIN COVI_APPROVAL4J.JWF_Performer AS C ON B.WorkItemID = C.WorkItemID INNER JOIN COVI_APPROVAL4J.JWF_ProcessDescription AS D ON A.ProcessDescriptionID = D.ProcessDescriptionID LEFT OUTER JOIN COVI_APPROVAL4J.jwf_domaindata AS E ON A.ProcessID = E.ProcessID LEFT OUTER JOIN COVI_SMART4J.sys_object_user AS U ON U.UserCode = A.InitiatorID LEFT OUTER JOIN COVI_APPROVAL4J.jwf_forms AS F ON D.FormID = F.FormID LEFT OUTER JOIN COVI_APPROVAL4J.jwf_formsschema AS S ON F.SchemaID = S.SchemaID WHERE B.DeputyID = #{userID} AND C.SubKind IN ('T000','T004','T005','T006','T008','T009','T011','T016','T017') AND C.State = 1 AND B.State = 288 AND A.ProcessState = 288 AND A.DeleteDate IS NULL
AND (D.BusinessData1 IS NULL
OR D.BusinessData1 IN
#{item}
)
AND D.BusinessData1 = #{businessData1}
) AS ApprovalList ORDER BY Priority DESC, Created DESC LIMIT 10

SELECT ProcessList.* FROM ( SELECT A.ProcessID , B.WorkItemID , C.PerformerID , A.FormInstID , A.ProcessDescriptionID , A.DocSubject AS FormSubject , D.FormPrefix , D.FormID , A.InitiatorID , covi_smart4j.Fn_BaseGetDictionary_S(#{lang}, A.InitiatorName) AS InitiatorName , C.UserCode , C.UserName , C.SubKind AS FormSubKind , DATE_FORMAT(A.StartDate, '%Y.%m.%d %H:%i') AS StartDate , DATE_FORMAT(IFNULL(B.Finished, ''), '%Y.%m.%d %H:%i') AS Finished , B.TaskID , E.DomainDataContext , U.PhotoPath , D.BusinessData1 , D.BusinessData2 , D.IsFile , D.IsComment FROM COVI_APPROVAL4J.JWF_Process AS A INNER JOIN COVI_APPROVAL4J.JWF_Workitem AS B ON A.ProcessID = B.ProcessID INNER JOIN COVI_APPROVAL4J.JWF_Performer AS C ON B.WorkItemID = C.WorkItemID INNER JOIN COVI_APPROVAL4J.JWF_ProcessDescription AS D ON A.ProcessDescriptionID = D.ProcessDescriptionID LEFT OUTER JOIN COVI_APPROVAL4J.jwf_domaindata AS E ON A.ProcessID = E.ProcessID LEFT OUTER JOIN COVI_SMART4J.sys_object_user AS U ON U.UserCode = A.InitiatorID WHERE C.UserCode = #{userID} AND C.SubKind IN ('T000','T004','T005','T006','T008','T009','T011','T016','T017','T018','T019','T020') AND C.State = 1 AND B.State = 528 AND A.ProcessState = 288 AND A.DeleteDate IS NULL
AND (D.BusinessData1 IS NULL
OR D.BusinessData1 IN
#{item}
)
AND D.BusinessData1 = #{businessData1}
UNION ALL
SELECT A.ProcessID , B.WorkItemID , C.PerformerID , A.FormInstID , A.ProcessDescriptionID , A.DocSubject AS FormSubject , D.FormPrefix , D.FormID , A.InitiatorID , covi_smart4j.Fn_BaseGetDictionary_S(#{lang}, A.InitiatorName) AS InitiatorName , C.UserCode , C.UserName , C.SubKind AS FormSubKind , DATE_FORMAT(A.StartDate, '%Y.%m.%d %H:%i') AS StartDate , DATE_FORMAT(IFNULL(B.Finished, ''), '%Y.%m.%d %H:%i') AS Finished , B.TaskID , E.DomainDataContext , U.PhotoPath , D.BusinessData1 , D.BusinessData2 , D.IsFile , D.IsComment FROM COVI_APPROVAL4J.JWF_Process AS A INNER JOIN COVI_APPROVAL4J.JWF_Workitem AS B ON A.ProcessID = B.ProcessID INNER JOIN COVI_APPROVAL4J.JWF_Performer AS C ON B.WorkItemID = C.WorkItemID INNER JOIN COVI_APPROVAL4J.JWF_ProcessDescription AS D ON A.ProcessDescriptionID = D.ProcessDescriptionID LEFT OUTER JOIN COVI_APPROVAL4J.jwf_domaindata AS E ON A.ProcessID = E.ProcessID LEFT OUTER JOIN COVI_SMART4J.sys_object_user AS U ON U.UserCode = A.InitiatorID WHERE B.DeputyID = #{userID} AND C.SubKind IN ('T000','T004','T005','T006','T008','T009','T011','T016') AND C.State = 1 AND B.State = 528 AND A.ProcessState = 288 AND A.DeleteDate IS NULL
AND (D.BusinessData1 IS NULL
OR D.BusinessData1 IN
#{item}
)
AND D.BusinessData1 = #{businessData1}
) AS ProcessList ORDER BY Finished DESC LIMIT 10

SELECT IFNULL(SUM(RewardVacDay), 0) AS RewardVacDay
FROM covi_smart4j.vm_vacationplan_history
WHERE UR_Code = #{userCode}
AND YEAR = #{year}

SELECT A.* FROM ( SELECT SiteLinkID , UserCode , SiteLinkName , SiteLinkURL , SortKey , Thumbnail FROM covi_smart4j.sys_site_link WHERE UserCode = #{userCode} ) AS A
SiteLinkName SiteLinkURL SiteLinkName
ASC DESC
SortKey ASC, SiteLinkID ASC
LIMIT #{pageSize} OFFSET #{pageOffset}
LIMIT #{listSize}

mapper.update:
UPDATE covi_smart4j.sys_site_link
SET SiteLinkName = #{siteLinkName}
  , SiteLinkURL = #{siteLinkURL}
  , SortKey = #{sortKey}

  , Thumbnail = #{thumbnail}

WHERE SiteLinkID = #{siteLinkID}

mapper.delete:
DELETE FROM covi_smart4j.sys_site_link
WHERE SiteLinkID IN

	#{siteLinkID}
