mapper.select:
SELECT AnniID AS MessageID,
REPLACE(AnniDate, '-', '.') AS AnniDate, AnniDateType, IF(AnniDateType = 'S','양력', IF(IsLeapMonth = 'Y','음력/윤달', '음력')) AS AnniDateTypeText, Subject, Priority, (CASE WHEN AnniDateType = 'S' THEN ( CASE WHEN DATEDIFF(AnniDate, now()) = 0 THEN 'D-Day' WHEN DATEDIFF(AnniDate, now()) > 0 THEN CONCAT('D', (-1 * DATEDIFF(AnniDate, now()))) ELSE CONCAT('D+', (-1 * DATEDIFF(AnniDate, now()))) END) ELSE ( CASE WHEN DATEDIFF((SELECT SolarDate FROM sys_calendar sc WHERE LunarDate = AnniDate AND sc.IsLeapMonth = soua.IsLeapMonth), now()) = 0 THEN 'D-Day' WHEN DATEDIFF((SELECT SolarDate FROM sys_calendar sc WHERE LunarDate = AnniDate AND sc.IsLeapMonth = soua.IsLeapMonth), now()) > 0 THEN CONCAT('D', (-1 * DATEDIFF((SELECT SolarDate FROM sys_calendar sc WHERE LunarDate = AnniDate AND sc.IsLeapMonth = soua.IsLeapMonth), now()))) ELSE CONCAT('D+', (-1 * DATEDIFF((SELECT SolarDate FROM sys_calendar sc WHERE LunarDate = AnniDate AND sc.IsLeapMonth = soua.IsLeapMonth), now()))) END) END) AS dDay,
(CASE WHEN Priority = 1 THEN '매우 높음' WHEN Priority = 2 THEN '높음' WHEN Priority = 3 THEN '보통' WHEN Priority = 4 THEN '낮음' ELSE '매우 낮음' END) AS PriorityText, DATEDIFF(AnniDate, AlarmDate) AS alarmDay, AlarmYN, Description, IsLeapMonth FROM covi_smart4j.sys_object_user_anniversary soua WHERE UserCode = #{userId}
AND AnniID = #{messageId}
AND Subject LIKE CONCAT('%', #{schTxt}, '%')
AND Description LIKE CONCAT('%', #{schTxt}, '%')
AnniDateTypeText Subject PriorityText alarmDay AnniDate
ASC DESC
LIMIT #{pageSize} OFFSET #{pageOffset}

	SELECT COUNT(AnniID) AS cnt
	  FROM covi_smart4j.sys_object_user_anniversary
	 WHERE UserCode = #{userId}

	 	AND AnniID = #{messageId}




			   AND Subject LIKE CONCAT('%', #{schTxt}, '%')


               AND Description LIKE CONCAT('%', #{schTxt}, '%')

mapper.insert:

	INSERT INTO covi_smart4j.sys_object_user_anniversary (
		UserCode, Subject, Description, 
		AlarmDate, 
		AnniDate, AnniDateType, Priority, AlarmYN
		,isLeapMonth
	)
    VALUES (
    	#{urCode}, #{subject}, #{description},


			    DATE_SUB(#{anniDate}, INTERVAL #{alramPeriod} DAY),


				'',


		#{anniDate}, #{anniDateType}, #{priority}, #{alarmYn}
		,#{isLeapMonth}
	)

