mapper.select:
SELECT SOF.MenuID AS groupValue ,( SELECT Fn_BaseGetDictionary_S(#{lang}, MultiDisplayName) FROM sys_object_menu WHERE 1=1 AND MenuID = SOF.MenuID) AS groupText , CONCAT(SOF.MenuID, '_', SOF.FolderID) AS optionValue , IFNULL(Fn_BaseGetDictionary_S(#{lang}, SOF.MultiDisplayName), SOF.DisplayName ) AS optionText FROM sys_object_folder AS SOF INNER JOIN board_config AS BC ON ( BC.FolderID = SOF.FolderID ) WHERE 1=1 AND BC.UseOwnerProcess = 'N' AND SOF.FolderType NOT IN ('Root', 'Folder', 'QuickComment', 'Contents') AND SOF.IsUse = 'Y' AND SOF.IsDisplay = 'Y' AND SOF.ObjectType = 'Board' AND SOF.DeleteDate IS NULL AND ( SOF.MenuID IN
#{item}
OR SOF.DomainID = 0 ) ORDER BY MenuID, FolderPath, SortKey ASC

SELECT BM.MessageID, BM.FolderID, BM.`Version`, BM.Subject, DATE_FORMAT(BM.RegistDate, '%Y-%m-%d %H:%i:%s') AS RegistDate, SOF.MenuID FROM board_message AS BM INNER JOIN sys_object_folder AS SOF ON BM.FolderID = SOF.FolderID WHERE BM.DeleteDate IS NULL AND SOF.DeleteDate IS NULL AND SOF.IsUse = 'Y' AND BM.MsgType NOT IN ('N', 'B') AND BM.MsgState IN ('C', 'A') AND BM.UseAnonym = 'N' AND (BM.ReservationDate IS NULL OR BM.ReservationDate < #{localCurrentDate}) AND (BM.UseSecurity != 'Y' OR (BM.UseSecurity = 'Y' AND BM.CreatorCode = #{sessionUserCode})) AND (BM.ExpiredDate IS NULL OR BM.ExpiredDate >= #{localCurrentDate} ) AND CreatorCode = #{userCode} AND BM.FolderID IN
#{item}
ORDER BY BM.RegistDate DESC LIMIT 5

SELECT OwnerCode AS managerCode,
IFNULL((SELECT ManagerCode FROM covi_smart4j.board_category WHERE FolderID = #{folderID} AND CategoryID = #{categoryID}), '') AS categoryManagerCode,
IFNULL(isAdminNotice, 'N') AS isAdminNotice FROM covi_smart4j.sys_object_folder WHERE 1=1 AND FolderID = #{folderID}
