			 AND c.DN_ID = #{DN_ID} 

				AND ca.Status = #{Status}


			UNION ALL 

			SELECT * FROM (
				SELECT  c.CU_ID, '' AS Status
				FROM community c
				WHERE c.CU_ID NOT IN (  SELECT CU_ID 
												FROM community c
												INNER JOIN community_affiliates ca ON ca.RequestID = c.CU_ID 
												WHERE 1=1
												AND c.AppStatus IN('RV','RS','UA','UD')	
												AND ca.ReceiveID = #{CU_ID}

