												WHERE 1=1
												AND c.AppStatus IN('RV','RS','UA','UD')	
												AND ca.RequestID = #{CU_ID}
										)
				AND c.CU_ID != #{CU_ID}
				AND c.AppStatus IN('RV','RS','UA','UD')
				AND ( c.GUBUN = 'C' OR c.GUBUN IS NULL )
				 AND c.DN_ID = #{DN_ID} 
			)A WHERE 1=1  AND Status = #{Status} 
	)A

