/* user.contents.updateTargetFolder */
    UPDATE sys_object_folder
    SET
    	MemberOf = #{memberOf}
    	, FolderPath = #{folderPath}
    	, SortKey = #{sortKey}
    	, SortPath = #{sortPath}
    WHERE FolderID = #{folderID}

UPDATE board_userform
SET    IsList = 'N'
WHERE FolderID = #{folderID}

