mapper.delete:
        DELETE
        FROM covi_smart4j.EVENT_RESOURCE_BOOKING

		    EventID = #{EventID}
		    AND DateID = #{DateID}
		    AND ResourceID = #{ResourceID}

DELETE
FROM covi_smart4j.EVENT_NOTIFICATION
WHERE
	EventID = #{EventID}
	AND DateID = #{DateID}

mapper.update:
        UPDATE covi_smart4j.EVENT
        SET
        	FolderID = #{FolderID}
		    ,FolderType = #{FolderType}
		    ,EventType = #{EventType}
		    ,LinkEventID = #{LinkEventID}
		    ,MasterEventID = #{MasterEventID}
		    ,Subject = #{Subject}
		    ,Description = #{Description}

		    ,Place = #{Place}

		    ,IsPublic = #{IsPublic}
		    ,IsInviteOther = #{IsInviteOther}
		    ,ImportanceState = #{ImportanceState}
		    ,OwnerCode = #{OwnerCode}
		    ,ModifierCode = #{ModifierCode}
		    ,ModifyDate = NOW(3)
        WHERE EventID = #{EventID}

        UPDATE covi_smart4j.EVENT
        SET
		    Subject = #{Subject}
		    ,Description = #{Description}

		    ,Place = #{Place}

		    ,IsPublic = #{IsPublic}
		    ,IsInviteOther = #{IsInviteOther}
		    ,ImportanceState = #{ImportanceState}
		    ,ModifierCode = #{ModifierCode}
		    ,ModifyDate = NOW(3)
        WHERE LinkEventID = #{EventID} 
        AND EventType = 'A'

        UPDATE covi_smart4j.EVENT_REPEAT
        SET
        	AppointmentStartTime = #{AppointmentStartTime}
			,AppointmentEndTime = #{AppointmentEndTime}
			,AppointmentDuring = #{AppointmentDuring}
			,RepeatType = #{RepeatType}
			,RepeatYear = #{RepeatYear}
			,RepeatMonth = #{RepeatMonth}
			,RepeatWeek = #{RepeatWeek}
			,RepeatDay = #{RepeatDay}
			,RepeatMonday = #{RepeatMonday}
			,RepeatTuesday = #{RepeatTuesday}
			,RepeatWednseday = #{RepeatWednseday}
			,RepeatThursday = #{RepeatThursday}
			,RepeatFriday = #{RepeatFriday}
			,RepeatSaturday = #{RepeatSaturday}
			,RepeatSunday = #{RepeatSunday}
			,RepeatStartDate = #{RepeatStartDate}
			,RepeatEndType = #{RepeatEndType}
			,RepeatEndDate = #{RepeatEndDate}
			,RepeatCount = #{RepeatCount}
			,RepeatAppointType = #{RepeatAppointType}
        WHERE
        	RepeatID = #{RepeatID}

        UPDATE covi_smart4j.EVENT_REPEAT
        SET RepeatStartDate = #{StartDate}
		  , RepeatEndDate = #{EndDate}
		  , AppointmentStartTime = #{StartTime}
		  , AppointmentEndTime = #{EndTime}
        WHERE
        	RepeatID = #{RepeatID}

        UPDATE covi_smart4j.EVENT_NOTIFICATION
        SET
        	IsNotification = #{IsNotification}
			,IsReminder = #{IsReminder}
			,ReminderTime = #{ReminderTime}
			,IsCommentNotification = #{IsCommentNotification}
			,MediumKind = #{MediumKind}

		    EventID = #{EventID}
		    AND DateID = #{DateID}
		    AND RegisterCode = #{RegisterCode}

mapper.select:
        SELECT 
			IsNotification
			,IsReminder
			,ReminderTime
			,IsCommentNotification
			,MediumKind
		FROM covi_smart4j.EVENT_NOTIFICATION

		    EventID = #{EventID}
		    AND DateID = #{DateID}
		    AND RegisterCode = #{RegisterCode}
		    AND RegisterKind = #{RegisterKind}

		LIMIT 1;

SELECT AppointmentStartTime ,AppointmentEndTime ,AppointmentDuring ,RepeatType ,RepeatYear ,RepeatMonth ,RepeatWeek ,RepeatDay ,RepeatMonday ,RepeatTuesday ,RepeatWednseday ,RepeatThursday ,RepeatFriday ,RepeatSaturday ,RepeatSunday ,RepeatStartDate ,RepeatEndType ,RepeatEndDate ,RepeatCount ,RepeatAppointType FROM covi_smart4j.EVENT_REPEAT
EventID = #{EventID} AND RepeatID = #{RepeatID}
LIMIT 1;

        SELECT RegisterCode
        FROM covi_smart4j.event_notification
		WHERE EventID = #{EventID}
		AND DateID = #{DateID}
		AND IsCommentNotification = 'Y'
		AND IsNotification = 'Y'

mapper.update:
        UPDATE covi_smart4j.event_resource_booking SET
		DateID = #{DateID},
		EventID = #{EventID}
		WHERE DateID = #{oldDateID}
		AND EventID = #{oldEventID};

mapper.delete:
DELETE FROM
covi_smart4j.event_relation
WHERE ScheduleID = #{ScheduleID}
AND ResourceID = #{ResourceID}

mapper.update:
UPDATE
	covi_smart4j.event_resource_booking
SET
	RealEndDateTime = (SELECT EndDateTime FROM covi_smart4j.event_date WHERE DateID = #{DateID}),
	ApprovalState = (SELECT CASE WHEN BookingType='DirectApproval' THEN 'Approval' ELSE 'ApprovalRequest' END FROM resource WHERE FolderID = Fn_ResourceParentIDGet_S(#{ResourceID}))
WHERE
	DateID = #{DateID}
	AND EventID = #{EventID}
	AND ResourceID = #{ResourceID}

mapper.select:
        SELECT IFNULL(GROUP_CONCAT(DateID),'') AS DateIDs
        FROM covi_smart4j.event_date
		WHERE EventID = #{EventID}

SELECT AclID, ObjectID, ObjectType, SubjectCode, SubjectType, AclList, Security, `Create`, `Delete`, `Modify`, `Execute`, `View`, `Read`, CASE WHEN SubjectType = 'CM' THEN '9' WHEN SubjectType = 'DEPT' THEN '2' WHEN SubjectType = 'UR' THEN '1' ELSE '3' END AS AclPriority FROM covi_smart4j.sys_object_acl AS a LEFT JOIN sys_object_group AS g ON a.SubjectCode = g.GroupCode AND a.SubjectType = 'GR' WHERE ObjectType = 'FD' AND ( (a.SubjectCode IN
#{item}
AND a.SubjectType != 'UR' ) OR (a.SubjectCode = #{userCode} AND a.SubjectType = 'UR') ) AND ObjectID IN (SELECT FolderID FROM SYS_OBJECT_FOLDER WHERE ObjectType = 'Resource' OR FolderType like 'Schedule%') ORDER BY AclPriority DESC, g.SortPath;
