mapper.select:
SELECT COUNT(*) FROM board_message AS BM LEFT JOIN sys_object_folder AS SOF ON BM.FolderID = SOF.FolderID LEFT JOIN board_config AS BC ON BM.FolderID = BC.FolderID LEFT JOIN BOARD_CONFIG_DEFAULT AS BCD ON SOF.FolderType = BCD.FolderType LEFT JOIN board_message_reader AS BMR ON BM.MessageID = BMR.MessageID AND BMR.ReaderCode = #{userID} WHERE BM.DeleteDate IS NULL AND SOF.DeleteDate IS NULL AND SOF.IsUse = 'Y' AND BM.IsCurrent = 'Y' AND BM.MsgType NOT IN ('N', 'B') AND BM.MsgState IN ('C', 'A') AND IF(SOF.FolderType = 'OneToOne', BM.OwnerCode = #{userID} OR (SELECT OwnerCode FROM sys_object_folder WHERE FolderID = BM.FolderID) LIKE CONCAT('%',#{userID},';%') , 1=1) AND IFNULL(BM.SecurityLevel,999) >= (SELECT SecurityLevel FROM sys_object_user WHERE UserCode = #{userID}) AND (BM.UseSecurity != 'Y' OR (BM.UseSecurity = 'Y' AND BM.CreatorCode = #{userID})) AND (BM.ExpiredDate IS NULL OR BM.ExpiredDate >= #{localCurrentDate} ) AND IFNULL(BC.UseIncludeRecentReg, BCD.UseIncludeRecentReg) = 'Y' AND BM.RegistDate >= DATE_ADD(NOW(), INTERVAL -14 DAY) AND BMR.ReaderCode IS NULL
AND BM.FolderID IN
#{item}
AND SOF.MenuID = #{boardMenuID}
